// https://gist.github.com/MoOx/9137295
%button-reset {
  border: none;
  margin: 0;
  padding: 0;
  width: auto;
  overflow: visible;

  background: transparent;

  // inherit font & color from ancestor
  color: inherit;
  font: inherit;

  // Normalize `line-height`. Cannot be changed from `normal` in Firefox 4+.
