/* scss-combine:core/swatch */
// none swatch value for non-required swatches
.form-field-swatch {
  .swatch-none {
    .swatch {
      overflow: hidden;
      border: 1px solid rgba(#000, 0.15);

      svg {
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
      }
    }
  }
}


// General
/* scss-combine:general/spinner */
.pxu-fading-circle {
  position: absolute;
  top: 50%;
  left: 50%;
  width: 60px;
  height: 60px;
  margin-top: rem(-30);
  margin-left: rem(-30);

  .pxu-circle {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;

    &:before {
      display: block;
      width: 15%;
      height: 15%;
      margin: 0 auto;
      background-color: $spinner-light-fill-color;
      border-radius: 100%;
      content: "";
      animation: pxu-circleFadeDelay 1.2s infinite ease-in-out both;
    }
  }

  &.spinner-dark {
    .pxu-circle:before {
      background-color: $spinner-dark-fill-color;
    }
  }

  .pxu-circle2 {
    transform: rotate(30deg);

    &:before {
      animation-delay: -1.1s;
    }
  }

  .pxu-circle3 {
    transform: rotate(60deg);

    &:before {
      animation-delay: -1s;
    }
  }

  .pxu-circle4 {
    transform: rotate(90deg);

    &:before {
      animation-delay: -0.9s;
    }
  }

  .pxu-circle5 {
    transform: rotate(120deg);

    &:before {
      animation-delay: -0.8s;
    }
  }

  .pxu-circle6 {
    transform: rotate(150deg);

    &:before {
      animation-delay: -0.7s;
    }
  }

  .pxu-circle7 {
    transform: rotate(180deg);

    &:before {
      animation-delay: -0.6s;
    }
  }

  .pxu-circle8 {
    transform: rotate(210deg);

    &:before {
      animation-delay: -0.5s;
    }
  }

  .pxu-circle9 {
    transform: rotate(240deg);

    &:before {
      animation-delay: -0.4s;
    }
  }

  .pxu-circle10 {
    transform: rotate(270deg);

    &:before {
      animation-delay: -0.3s;
    }
  }

  .pxu-circle11 {
    transform: rotate(300deg);

    &:before {
      animation-delay: -0.2s;
    }
  }

  .pxu-circle12 {
    transform: rotate(330deg);

    &:before {
      animation-delay: -0.1s;
    }
  }
}


@keyframes pxu-circleFadeDelay {
  0%,
  39%,
  100% { opacity: 0; }

  40% { opacity: 1; }
}

/* scss-combine:general/scaffolding */
.container {
  @extend %clearfix;
  max-width: $max-width + ($gutter * 2);
  padding: 0 $gutter;
  margin: 0 auto;
}

.columns {
  @extend %clearfix;
  @include row($max-width);
}

.main-content {
  @extend %clearfix;
  position: relative;
  max-width: $outer-width;
  background: $container-background-color;

  @include breakpoint(m-up) {
    margin-top: 20px;
    margin-right: $gutter;
    margin-left: $gutter;
  }

  @include media($min: 1140px) {
    margin-right: auto;
    margin-bottom: $gutter * 2;
    margin-left: auto;
  }

  .has-carousel & {
    @include breakpoint(m-up) { margin-top: -30px; }
    @include media($min: 1140px) { margin-top: -78px; }
  }
}

// section blocks
.section {
  padding: 34px 0;
  margin: 34px 0;
}

/* scss-combine:general/general */
html {
  font-size: $body-font-size;
}

body {
  font-family: $body-font-family;
  line-height: 1.666;
  color: $body-color;
  background: $body-background-color;

  @include media($max: $mobile-menu-breakpoint) {
    &.mobile-menu-active {
      overflow: hidden;
    }
  }

  @if ($body-font-family == "Fira Sans") {
    font-weight: 300;
  }

  &.scroll-locked { overflow: hidden; }
}

// Links

a {
  color: $link-color;

  &:hover { color: $link-hover-color; }
}

// Headers

h1,
h2,
h3,
h4,
h5,
h6 {
  font-size: 1rem;
  font-weight: normal;
  line-height: 1.25;
}

// Basic elements

hr {
  height: 1px;
  background: $border-color;
}

cite { color: $meta-color; }

img { vertical-align: middle; }

table { border-color: $border-color; }

td { border-color: $border-color; }

svg { fill: currentColor; }

// hide from screen readers

.sr-only {
  @extend .show-for-sr;
}

// Prep for dynamic js Excerpt
// TODO: depending on performance, this should really be wrapped in the .js flag

.has-excerpt {
  display: none;

  &.loaded { display: block; }
}

.fb-like iframe {
  max-width: none;
}

// GeoTrust seal
table.geotrust-ssl-seal-table {
  margin-top: 20px;
  margin-bottom: 0;
  border: 0;

  @include breakpoint(m-only) {
    position: relative;
    left: 50%;
    transform: translateX(-50%);
  }

  td {
    padding: 0;
    border: 0;
  }
}

.icon-sprite {
  display: none;
}
/* scss-combine:general/alerts */
.alert {
  position: relative;
  padding: 15px;
  margin-bottom: 1em;
  font-size: em(13);
  border-radius: 3px;
  transition: opacity 0.3s ease;

  &.dismissed { opacity: 0; }

  &.hidden { display: none; }

  .alert-dismiss {
    position: absolute;
    top: 4px;
    right: 6px;
    font-size: em(16, 13);
    line-height: 1;
    color: inherit;
    cursor: pointer;
    opacity: 0.8;

    &:hover { opacity: 1; }
  }

  &.alert-success {
    color: darken($success-color, 10%);
    background-color: rgba($success-color, 0.1);

    .alert-dismiss { color: darken($success-color, 10%); }
  }

  &.alert-error {
    color: darken($error-color, 10%);
    background-color: rgba($error-color, 0.1);

    .alert-dismiss { color: darken($error-color, 10%); }
  }

  &.alert-info {
    color: lighten($body-color, 20%);
    background-color: rgba($body-color, 0.05);

    .alert-dismiss { color: lighten($body-color, 20%); }
  }

  // make buttons small & tidy
  // currently only used in the 'cart add' success alert
  .button {
    padding: 8px 10px 7px;
    font-weight: normal;
    color: $container-background-color;
    background: darken($success-color, 4%);
    border-color: darken($success-color, 4%);

    &:hover {
      background: darken($success-color, 15%);
      border-color: darken($success-color, 15%);
    }
  }

  .actions {
    @extend %clearfix;
    @include row(100, 4);
    margin-top: 1em;

    a {
      @include fluid-width(48, 100, 4);
      display: block;
      float: left;
    }
  }
}

/* scss-combine:general/button */
button {
  @extend %button-reset;
}

.button {
  padding: rem(10) rem(20);
  font-family: $button-font;
  font-size: (13 / 14) * 1rem;
  font-weight: $button-weight;
  letter-spacing: 0.1em;
  color: $container-background-color;
  text-transform: uppercase;
  background: transparent;
  border-style: solid;
  border-width: 1px;
  border-radius: 3px;
  transition: all 0.1s ease-in;

  &:focus { outline: 0; }
}

.button-disabled,
.button:disabled {
  &,
  &:hover {
    cursor: default;
    background: $button-disabled-color;
    border-color: $button-disabled-color;
  }
}

.button-wide {
  display: block;
  width: 100%;
}

.button-small {
  @if ($heading-font-family != "Poppins") {
    font-family: $body-font-family;
  }
}

.button-large {
  padding-top: rem(14);
  padding-bottom: rem(13);
  font-size: (14 / 12) * 1rem;
  line-height: rem(33);
}

// solid button, white text
.button-primary {
  color: $button-primary-text-color;
  background-color: $button-primary-background-color;
  border-color: $button-primary-border-color;

  &:hover,
  &:focus {
    color: $button-primary-text-color;
    border-color: mix(#000, $button-primary-background-color, 8%);
    background-color: mix(#000, $button-primary-background-color, 8%);
  }
}

// white bg, colored border & text
.button-secondary {
  color: $button-secondary-text-color;
  background-color: $button-secondary-background-color;
  border-color: $button-secondary-border-color;

  &:hover,
  &:focus {
    color: $container-background-color;
    background-color: $button-secondary-border-color;
  }
}

// reset button element to plain text
.button-link {
  padding: 0;
  color: $link-color;
  background: transparent;
  border: 0;
  border-radius: 0;
  box-shadow: none;

  &:focus { outline: 0; }
}

// add to cart ajax progress
.button-progress {
  transition: opacity 0.2s;

  .pxu-fading-circle {
    position: relative;
    top: auto;
    left: auto;
    display: inline-block;
    width: 0;
    height: 1.57em; // 22/14
    margin: auto;
    vertical-align: middle;
    content: "";
    opacity: 0;
    transition: all 0.4s ease;
  }

  .pxu-circle {
    &:before {
      width: 10%;
      height: 20%;
    }
  }

  &.progress {
    color: $container-background-color;
    cursor: default;
    background: desaturate($button-primary-background-color, 100%);
    border-color: desaturate($button-primary-background-color, 100%);
    opacity: 0.5;

    .pxu-fading-circle {
      width: 2.14em; // 30/14
      opacity: 1;
    }
  }
}

/* scss-combine:general/form */

// hide dummy containers for calculating select box widths
.select-tmp {
  position: absolute;
  left: -9999em;
}

// hide dynamically added selectwrapper text, which we aren't using
.form-selected-text { display: none; }

// Add star to required fields
.form-field.required {
  .form-input-title,
  .product-option-title {
    &:after {
      font-size: em(12);
      color: $error-color;
      content: "*";
    }
  }
}

// core style for all inputs
.form-input {
  display: block;
  max-width: 100%;
  padding-right: rem(10);
  padding-left: rem(10);
  line-height: em(20);
  color: $input-color;
  background: $input-background-color;
  border-color: $input-border-color;
  border-radius: 3px;
  transition: border-color 0.2s $ease-out-quad;

  &::placeholder {
    color: lighten($input-color,40%);
  }

  &:focus {
    border-color: contrast($input-border-color, 10%);
    outline: none;
  }
}

// everything but radios & checkboxes get heights
#{$all-text-inputs} {
  &.form-input {
    padding-top: em(10);
    padding-bottom: em(9);
  }
}

// append a submit button inline with an input
.input-appended {
  @extend %clearfix;
  position: relative;

  .form-input-wrapper {
    position: absolute;
    top: 0;
    left: 0;
  }

  #{$all-text-inputs} { width: 100%; }

  .button {
    float: right;
    padding-right: 0;
    padding-left: 0;
  }
}

// scss-lint:disable QualifyingElement
input[type="file"] {
  width: 100%;
  padding: 10px;
  font-size: em(12);
}

// put radio buttons & labels into a list
.form-field-checkbox,
.form-field-radio {
  .form-label-text {
    display: inline-block;
    margin-bottom: 0;
  }

  .form-label:not(:last-child) {
    margin-bottom: em(8);
  }

  .form-input {
    display: inline-block;
    width: auto;
    margin-right: 2px;
  }
}

.form-inline-message {
  display: block;
  padding: 5px 0 0;
  font-size: rem(12);

  .form-field-invalid & {
    color: $error-color;
  }
}


// ---------------------------------------------------------------- */
// Dynamic-width select inputs (currency & product sorting)

.select-input-wrapper {
  position: relative;
  padding-right: em(16, 12);

  &:after {
    @include icon-arrow-down($input-color);
    position: absolute;
    top: 48%;
    right: 0;
    display: block;
    width: 12px;
    height: 12px;
    transform: translateY(-50%);
  }

  select {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    z-index: 2;
    width: 100%; // needed for Firefox
    height: 100%; // needed for IE
    text-transform: none;
    cursor: pointer;
    opacity: 0;
    appearance: none;
  }

  .form-selected-text {
    display: block;
  }
}


// ---------------------------------------------------------------- */
// General Form Layout

.form-field-control {
  @extend %clearfix;
}

#{$all-text-inputs},
.form-select {
  width: 100%;
}

.form-field { margin-bottom: 16px; }

// Applied to labels used as option text (radios/checkboxes)
.form-label-text {
  font-weight: 300;
  vertical-align: middle;
}

.form-field-group {
  padding: 10px 12px;
  border: 1px solid $input-border-color;
  border-radius: 3px;
}

.form-select,
.input-select { height: em(40); }

// inline input types
.form-field-product-list,
.form-field-checkbox,
.form-field-radio {
  .form-field-control { display: inline-block; }

  .form-label:not(:last-child) {
    margin-bottom: em(8);
  }

  .form-input {
    display: inline-block;
    width: auto;
  }
}


// ---------------------------------------------------------------- */
// Date Field Components

.form-field-date {
  @extend %clearfix;

  .form-select-wrapper { float: left; }

  .form-date-year { width: 44%; }

  .form-date-month {
    width: 25%;
    margin-right: 3%;
    margin-left: 3%;
  }

  .form-date-day { width: 25%; }
}


// ---------------------------------------------------------------- */
// Basic form grid

.form-row {
  @include breakpoint(s-up) {
    @extend %clearfix;
    @include row(600px);
  }
}

.form-column-half {
  @include breakpoint(s-up) {
    @include fluid-width(290px, 600px);
    float: left;
  }
}

// ---------------------------------------------------------------- */
// Small forms

.form-field-small {
  margin-bottom: 12px;
  font-size: em(13);

  .form-input {
    padding-right: 6px;
    padding-left: 6px;
  }

  #{$all-text-inputs} {
    &.form-input {
      padding-top: 7px;
      padding-bottom: 8px;
    }
  }

  .form-select { height: rem(34); }
}

.form-field-centered { text-align: center; }

.form-action-secondary {
  font-size: em(13);
  font-weight: 300;
  text-align: right;
}

/* scss-combine:general/page */
.page-container {
  @extend %clearfix;
  padding-top: 20px;
  margin: 0 auto 40px;


  @include breakpoint(s-up) {
    padding-top: 30px;
    margin-bottom: 80px;
  }

  @include breakpoint(l-up) {
    padding-top: 60px;
    margin-bottom: 130px;
  }
}

// narrower layout with centered title
// used for cms pages and longer forms
.page-narrow {
  max-width: 600px;
  margin-right: auto;
  margin-left: auto;
}

.page-heading {
  @extend %heading-large;
  margin-top: 20px;
  margin-bottom: 30px;

  @include breakpoint(s-up) {
    margin-bottom: 40px;
    font-size: em(30 * $heading-font-size-modifier);
  }

  @include breakpoint(m-up) {
    margin-top: 25px;
    margin-bottom: 48px;
    font-size: em(36 * $heading-font-size-modifier);
  }

  .page-narrow & { text-align: center; }
}

// smaller size for secondary/utility pages
.page-heading-small {
  margin-top: 10px;
  font-size: em(28 * $heading-font-size-modifier);
}

// upper section for page / form messages & feedback
.page-message-area {
  margin-bottom: $gutter * 2;
  text-align: center;
}

.section-header {
  margin-bottom: 42px;
  text-align: center;
}

.section-title {
  @extend %heading-medium;
  margin: 0 0 12px;
}

/* scss-combine:general/modals */
// turn modal into more of a full-screen kinda thing
@include breakpoint(s-only) {
  .modal {
    position: absolute;
    top: 10px;
    right: 10px;
    left: 10px;
    // override programmatic margin for fullscreen
    // scss-lint:disable ImportantRule
    margin: 0 !important;
  }

  .modal-wrapper.loaded {
    .pxu-fading-circle {
      opacity: 0;
    }
  }
}

.modal-overlay { background-color: rgba(#000, 0.7); }

.modal-content {
  position: relative;
  z-index: 1;
  padding: 20px;
  background: $container-background-color;

  .modal-close {
    position: absolute;
    top: 21px;
    right: 21px;
    font-size: em(20);
    line-height: 1;
    color: rgba($body-color, 0.5);

    @include breakpoint(s-only) {
      top: 16px;
      right: 16px;
      font-size: em(16);
    }

    .icon-close {
      width: 18px;
      height: 18px;
      fill: currentColor;
    }

    &:hover { color: rgba($body-color, 0.8); }
  }
}

/* scss-combine:general/sharing */
.share-links { cursor: default; }

.share-link {
  display: inline-block;
  margin: 0 4px;
  line-height: 1;
  vertical-align: middle;

  a {
    display: inline-block;
    vertical-align: bottom;
  }

  .fb-like {
    padding-right: rem(14);
    border-right: 1px solid $border-color;
  }

  .share-icon {
    width: 14px;
    height: 14px;
  }
}

/* scss-combine:general/rte */
.rte {
  @extend %clearfix;
  overflow-x: auto;
  font-size: $body-font-size;
  text-align: left;

  h1 { font-size: 2em; }
  h2 { font-size: 1.5em; }
  h3 { font-size: 1.17em; }

  h4 {
    font-family: $body-font-family;
    font-size: 1.12em;
    color: mix($body-color, $container-background-color);
  }

  h5 {
    font-family: $body-font-family;
    font-size: 0.83em;
  }

  h6 {
    font-family: $body-font-family;
    font-size: 0.75em;
    letter-spacing: 0.02em;
    text-transform: uppercase;
  }

  > *:first-child {
    margin-top: 0;
  }

  > *:last-child {
    margin-bottom: 0;
  }

  a:hover {
    text-decoration: underline;
  }

  hr { margin: 2em 0; }

  ul { list-style: disc; }

  ul,
  ol,
  dl {
    margin-left: 20px;
  }

  blockquote {
    padding-left: 20px;
    margin-left: 0;
    border-left: 2px solid $border-color;
  }

  img {
    height: auto;
    max-width: 100%;
  }

  // override list styling for external rss feed
  .rss-feed {
    margin: 0;
  }

  .rss-feed-item {
    display: block;
    margin-bottom: em(30);
    border-bottom: 1px solid $border-color;
    padding-bottom: em(20);
  }
}


// Module overrides
/* scss-combine:modules/carousel */
$carousel-small-text: 600px;

.carousel {
  position: relative;
  transition: height 0.5s ease;

  @include breakpoint(m-up) {
    .carousel-aspect-three-to-one & {
      @include maintain-ratio(3 1);
    }

    .carousel-aspect-five-to-two & {
      @include maintain-ratio(5 2);
    }

    .carousel-aspect-two-to-one & {
      @include maintain-ratio(2 1);
    }

    .carousel-aspect-five-to-three & {
      @include maintain-ratio(5 3);
    }
  }
}

.carousel-item {
  display: none;
  height: 100%;
  color: $carousel-color;
  background-color: $container-background-color;
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  opacity: 0;

  &.animating {
    display: block;
    transition: opacity 1s ease;
  }

  &.visible {
    z-index: auto;
    display: block;
    opacity: 1;
  }

  // when we're forcing a ratio, slides conform to the parent .carousel's dimensions
  .carousel-has-aspect & {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;

    // hide img tag rely on background image
    .carousel-item-image {
      @extend .show-for-sr;
    }
  }

  // when we're relying on image sizes, we ditch the bg image and the absolute positioning.
  .carousel-no-aspect & {
    position: relative;
    background-image: none !important;

    &.animating-in {
      position: absolute;
    }
  }
}

.carousel-item-info {
  display: flex;
  flex-direction: column;
  justify-content: center;
  top: 0;
  height: 100%;
  opacity: 0;
  transition: opacity 0.4s ease 0.4s, transform 0.4s ease 0.4s;
  overflow: hidden;

  @include breakpoint(m-up) {
    height: calc(100% - 30px); // offset the 30px neg margin on .main-content
  }

  @include media($min: 1140px) {
    height: calc(100% - 78px); // offset the 78px neg margin on .main-content
  }

  .animating-out & {
    opacity: 0;
    transition-delay: 0s, 0s;
  }

  .visible & { opacity: 1; }

  .carousel-justify-center & {
    left: $gutter;
    width: calc(100% - #{$gutter * 2});
    text-align: center;
    transform: translateY(2%); // animate up and down when centered

    @include breakpoint(s-up) {
      left: 50%;
      width: 70%;
      margin-left: -35%;
    }
  }

  .carousel-justify-center .visible & {
    transform: translateY(0);
  }

  .carousel-justify-left & {
    width: 100%;
    transform: translateX(2%); // animate side-to-side when left-aligned
  }

  .carousel-justify-left .visible & {
    transform: translateX(0);
  }

  @include breakpoint(s) {
    .primary-button {
      margin-bottom: 20px;
    }
  }
}

.carousel-item-info-inner {
  .carousel-justify-left & {
    width: 100%;
    max-width: $outer-width + ($gutter * 2);
    padding-right: $gutter * 2;
    padding-left: $gutter * 2;
    margin: 0 auto;
  }
}

.carousel-item-title {
  margin: 0 0 em(20, 42);
  font-family: $carousel-heading-font;
  font-size: em(26);
  font-weight: $heading-font-weight;
  line-height: 1.2;

  @include media($min: $carousel-small-text) {
    font-size: rem(42);

    .carousel-font-size-regular & {
      font-size: rem(63);
    }

    .carousel-font-size-large & {
      font-size: rem(70);
    }
  }

  @if ($carousel-heading-font == "Roboto" or $carousel-heading-font == "Titillium Web") {
    font-weight: 300;
  }
}

.carousel-item-caption {
  margin: 0 auto 18px;
  font-size: em(13);
  font-weight: 300;

  .carousel-justify-left & {
    margin-left: 0;
  }

  @include media($min: $carousel-small-text) {
    margin-top: 20px;
    margin-bottom: 28px;
    font-size: em(16);

    .carousel-font-size-regular & {
      font-size: rem(17);
    }

    .carousel-font-size-large & {
      font-size: rem(19);
    }
  }

  @include breakpoint(m-up) {
    width: 50%;
  }
}

.carousel-link {
  position: absolute;
  z-index: 1;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  text-indent: -9999px;
}

.carousel-navigation {
  position: static;
}

.carousel-navigation-item {
  top: 0;
  bottom: 0;
  z-index: 2;
  display: block;
  width: 10%;
  text-indent: -9999px;
  background: $container-background-color;
  opacity: 0;
  transition: opacity 0.2s ease;

  &.previous { left: 0; }

  &.next { right: 0; }
}

.carousel-pagination {
  right: 0;
  bottom: 18px;
  left: 0;
  text-align: center;

  // these mirror the breakpoints on .main-content
  @include breakpoint(s-up) { bottom: 10px; }
  @include breakpoint(m-up) { bottom: 62px; }
  @include media($min: 1140px) { bottom: 100px; }
}

.carousel-pagination-item {
  position: relative;
  z-index: 2;
  display: inline-block;
  width: 10px;
  height: 10px;
  margin: 0 6px;
  text-indent: -9999px;
  vertical-align: bottom;
  cursor: pointer;
  background-color: rgba(#000, 0.4);
  border: 2px solid $container-background-color;
  border-radius: 50%;
  transition: background-color 0.2s ease;
  box-shadow: 0 0 8px rgba(#000, 0.3);

  &.active {
    background-color: $container-background-color;
  }
}

.no-js {
  .carousel-item:first-child {
    position: static;
  }

  .carousel-item-info {
    position: relative;
  }
}

/* scss-combine:modules/instagram */
.instagram-photo-container {
  @extend %clearfix;
  @include row(1018, 30);
}

.instagram-column {
  @include breakpoint(s-up) {
    @include row(494, 30);
    @include fluid-width(509, 1018, 0);
    float: left;
  }
}

.instagram-photo {
  @include fluid-width(232, 494, 30);
  position: relative;
  display: block;
  float: left;
  padding: 22.135% 0; // 1:1 aspect ratio
  margin-bottom: (30/494) * 100%;
  overflow: hidden;
  background: #eee;

  @include breakpoint(s-up) {
    &.photo-2,
    &.photo-3 {
      @include fluid-width(494, 494, 30);
      padding: 47.275% 0; // 1:1 aspect ratio
    }
  }

  // fadein when ajax & image loading are complete
  img,
  .caption {
    transition: opacity 0.2s ease-in-out 0s;

    .loading & {
      opacity: 0;
    }
  }

  img {
    position: absolute;
    top: 0;
    left: 0;
    max-width: 100%;
  }

  .caption {
    position: absolute;
    right: 0;
    bottom: 0;
    left: 0;
    padding: 10px 10px 8px;
    color: #fff;
    background: rgba(#000, 0.3);
  }

  .text {
    @extend %antialiased;
    overflow: hidden;
    font-weight: 600;
    text-overflow: ellipsis;
    white-space: nowrap;
  }

  .timestamp {
    font-size: em(12);
    font-weight: 300;
    opacity: 0.7;
  }
}

/* scss-combine:modules/baguette-box */
/*!
 * baguetteBox.js
 * @author  feimosi
 * @version 1.4.1
 * @url https://github.com/feimosi/baguetteBox.js
 */

// scss-lint:disable QualifyingElement, IdSelector, SelectorFormat

%arrow-button {
  top: calc(50% - 30px);
  width: 44px;
  height: 60px;
}

#baguetteBox-overlay {
  position: fixed;
  top: 0;
  left: 0;
  z-index: 1000000;
  display: none;
  width: 100%;
  height: 100%;
  overflow: hidden;
  background-color: rgba(#000, 0.8);
  opacity: 0;
  transition: opacity 0.5s ease;

  &.visible { opacity: 1; }

  .full-image {
    position: relative;
    display: inline-block;
    width: 100%;
    height: 100%;
    text-align: center;

    figure {
      display: inline;
      height: 100%; // Opera 12 image stretching fix
      margin: 0;
    }

    img {
      display: inline-block;
      width: auto;
      height: auto;
      max-width: 100%;
      max-height: 90%;
      vertical-align: middle;
      box-shadow: 0 0 8px rgba(#000, 0.6);
    }

    figcaption {
      position: absolute;
      bottom: 0;
      display: block;
      width: 100%;
      padding-bottom: 16px;
      font-weight: 300;
      line-height: 1.8;
      color: $container-background-color;
      text-align: center;
      white-space: normal;
    }

    &:before {
      display: inline-block;
      width: 1px;
      height: 50%;
      margin-right: -1px;
      content: "";
    }
  }
}

#baguetteBox-slider {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  white-space: nowrap;
  transition: left 0.4s ease, transform 0.4s ease;

  &.bounce-from-right {
    animation: bounceFromRight 0.4s ease-out;
  }

  &.bounce-from-left {
    animation: bounceFromLeft 0.4s ease-out;
  }
}

@keyframes bounceFromRight{
  0% { margin-left: 0; }
  50% { margin-left: -30px; }
  100% { margin-left: 0; }
}

@keyframes bounceFromLeft{
  0% { margin-left: 0; }
  50% { margin-left: 30px; }
  100% { margin-left: 0; }
}

.baguetteBox-button {
  position: absolute;
  padding: 0;
  margin: 0;
  font-size: 1.6em;
  color: #ddd;
  cursor: pointer;
  background-color: transparent;
  border: 0;
  border-radius: 15%;
  outline: none;
  transition: background-color 0.4s ease;

  &:hover { background-color: rgba($container-background-color, 0.1); }

  &#next-button {
    @extend %arrow-button;
    right: 2%;
  }

  &#previous-button {
    @extend %arrow-button;
    left: 2%;
  }

  &#close-button {
    top: 20px;
    right: 20px;
    width: 30px;
    height: 30px;
  }

  // Firefox fix
  svg {
    position: absolute;
    top: 0;
    left: 0;
  }
}

// http://tobiasahlin.com/spinkit/
.spinner {
  position: absolute;
  top: 50%;
  left: 50%;
  display: inline-block;
  width: 40px;
  height: 40px;
  margin-top: -20px;
  margin-left: -20px;
}

.double-bounce1,
.double-bounce2 {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: #fff;
  border-radius: 50%;
  opacity: 0.6;
  animation: bounce 2s infinite ease-in-out;
}

.double-bounce2 { animation-delay: -1s; }

@keyframes bounce {
  0%, 100% { transform: scale(0); }

  50% { transform: scale(1); }
}

/* scss-combine:modules/amp-product-options */
