    width: 22px;
    height: 22px;
  }

  .cart-count {
    display: inline-block;
    min-width: em(10, 14);
    margin-left: em(2, 14);
    line-height: em(22, 14);
    text-align: center;

    &:empty { min-width: 0; }
  }
}

.cart-dropdown-container { color: $body-color; }

/* scss-combine:general/header-desktop */
@include media($min: $mobile-menu-breakpoint) {

  .main-header { padding: 20px 0 14px; }

  .navigation-container {
    @extend %clearfix;
    padding-top: 23px;
    padding-right: $cart-buttons-width * 2;
    clear: both;

    .logo-center & {
      padding-right: 0;
      text-align: center;
    }
  }

  .header-primary-container {
    .logo-center & {
      text-align: center;
    }
  }

  .branding {
    float: left;

    .text {
      height: $branding-height;
      padding-top: 9px;
    }

    .retina-logo & img { max-width: 50%; }

    .logo-right & {
      float: right;
      text-align: right;
    }

    .logo-center & {
      float: none;
      display: inline-block;
      margin: 0 auto;
      text-align: center;

      .retina-logo & img { max-width: 50%; }
    }
  }


  // ------ Search button ------ //

  .search-form-container {
    position: absolute;
    right: $gutter + 72px;
    bottom: 0;
    z-index: 10;
    width: 34px;
    transition: width 0.2s ease;

    .search-form-input-wrap {
      transition: background 0.2s ease;

      .form-input {
        padding-left: em(36);
        opacity: 0;
        transition: opacity 0.2s ease;
      }
    }

    &.focus {
      width: 196px;

      .search-form-input-wrap {
        background-color: mix($header-text-color, $header-background-color, 20%);
        transition-delay: 0.1s;
      }

      .form-input { opacity: 1; }
    }

    .logo-center & {
      top: 0;
      bottom: auto;
    }
  }


  // ------ navigation ------ //

  .navigation {
    float: left;

    .nav-menu {
      display: inline-block;
      margin-left: -10px;
    }

    .logo-center & {
      float: none;
    }
  }

  .nav-menu-item {
    position: relative;
    display: inline-block;
    margin: 0 -10px;

    // first-level links, always visible in menu
    > a {
      @include optional-heading-font($menu-use-heading-font);

      display: block;
      padding: 8px 20px;
    }

    .caret {
      font-size: em(10);
    }

    // hover state for top-level links with dropdown
    &.menu-dropdown:hover > a,
    &.menu-dropdown.tapped > a {
      color: $link-color;
      background: $container-background-color;
      box-shadow: 0 0 3px rgba(0,0,0,0.2);

      .caret::before {
        @include icon-arrow-down($link-color);
      }
    }

    // transition & hover state for top-level non-dropdown links
    &:not(.menu-dropdown) > a {
      transition: opacity 0.1s ease-out;

      &:hover { opacity: 0.7; }
    }

  }

  // container for dropdown menu
  .dropdown {
    position: absolute;
    top: 100%;
    left: 0;
    display: none;
    min-width: calc(100% + 140px);
    padding: 22px 22px 18px;
    color: $link-color;
    text-align: left;
    background: $container-background-color;
    box-shadow: 0 2px 3px rgba(0,0,0,0.2);

    a {
      display: block;
      margin-bottom: 4px;
      color: $link-color;
      transition: color 0.1s ease-out;

      &:hover { color: $link-hover-color; }
    }

    .nav-menu-item:hover &,
    .nav-menu-item.tapped & {
      z-index: 3;
      display: block;
    }
  }

  .subnav-menu-item {
    &:not(:last-child) { margin-bottom: 10px; }

    // top-level dropdown items
    > a { font-weight: 600; }
  }

  .sub-subnav-menu-item {
    margin-left: 8px;
    // second-tier menu items
    > a { font-size: em(16, 17); }
  }

  // third-tier menu items
  .sub-sub-subnav-menu-item {
    margin-left: 8px;

    > a { font-size: em(15, 17); }

    li {
      padding-left: 8px;
    }

    a {
      font-size: em(15, 17);
    }
  }

  // hide mobile-only elements
  .open-dropdown,
  .subnavigation-container {
    display: none;
  }


  // ------ User links & currency selector ------ //

  .header-tools {
    position: absolute;
    font-size: em(12);

    .logo-left & {
      top: -8px;
      right: $gutter;
    }

    .logo-center &,
    .logo-right & {
      top: 0;
      left: $gutter;
    }
  }

  .currency-selector-wrapper {
    position: relative;
    display: inline-block;

    &:after {
      @include icon-arrow-down($header-text-color);
      right: 0;
    }
  }

  .form-input.currency-selector {
    @extend %reset-select;
    min-width: 40px;
    color: $header-text-color;
  }

  .customer-links {
    display: inline-block;
    margin-left: em(8, 12);

    a {
      display: inline-block;
      padding: 6px;
      color: mix($header-text-color, $header-background-color, 70%);

      &:hover { color: $header-text-color; }
    }
  }

  // ------ cart link ------ //

  .view-cart {
    display: flex;
    height: em(39);
    min-width: 46px;
    padding: em(8) 12px em(8) 14px;
    text-align: center;
  }

  .header-cart-container {
    position: absolute;
    right: $gutter;
    bottom: 0;
    display: block;

    .cart-dropdown-container {
      position: absolute;
      top: em(39);
      right: 0;
      z-index: 20;
      display: none;
      background: $container-background-color;
      box-shadow: 0 2px 3px rgba(0,0,0,0.2);
    }

    &:hover {
      .view-cart {
        color: $link-color;
        background: $container-background-color;
        box-shadow: 0 0 3px rgba(0,0,0,0.2);
      }

      .cart-dropdown-container { display: block; }
    }

    .logo-center & {
      top: 0;
      bottom: auto;
    }
  }

  .mobile-menu-close { display: none; }
}

/* scss-combine:general/header-mobile */
@include media($max: $mobile-menu-breakpoint) {

  .main-header {
    min-height: 64px;
    padding-bottom: 10px;

    .container {
      padding-right: 14px;
      padding-left: 14px;
    }
  }

  .header-primary-container {
    position: fixed;
    top: 0;
    right: 0;
    left: 0;
    z-index: 10;
    padding: 16px 0;
    background: $header-background-color;

    // less janky for slow JS loading
    .no-js & { position: static; }
  }

  .menu-open {
    position: absolute;
    top: 14px;
    left: 14px;
    width: em(23);

    &:before,
    &:after {
      content: "";
    }

    &:before,
    &:after,
    .menu-icon {
      display: block;
      height: 2px;
      margin: em(4) 0;
      background: $header-text-color;
    }
  }

  .branding {
    text-align: center;

    .text { font-size: em(18); }

    .logo {
      display: inline-block;
    }

    img { max-height: 50px; }
  }


  // ------ Search button ------ //

  .search-form-container {
    height: $branding-height;
    margin-top: 8px;
    clear: both;

    .search-form-input-wrap {
      background-color: mix($header-text-color, $header-background-color, 10%);
    }

    // scss-lint:disable QualifyingElement
    input.form-input {
      width: 100%;
      padding-left: 36px;
    }
  }

  // ------ Cart & popout ------ //

  .view-cart {
    position: absolute;
    top: 4px;
    right: 12px;
    display: block;
    width: 56px;
    padding: 8px 0;
    text-align: right;

    &:hover { color: inherit; }

    .cart-icon {
      top: 5px;
    }
  }

  .cart-count:empty + .cart-icon {
    right: 0;
    left: auto;
  }

  .cart-dropdown-container {
    position: fixed;
    top: 0;
    right: 0;
    z-index: 20;
    display: none;
    width: 320px;
    height: 100%;
    background: $container-background-color;

    &.animating,
    &.visible {
      display: block;
      transform: translateX(100%);
      transition: transform 0.3s ease-in-out;
    }

    &.animating-out { transition-duration: 0.2s; }

    &.visible { transform: translateX(0); }
  }

  .cart-footer { padding: 17px 18px; }

  .navigation-container {
    position: fixed;
    top: 0;
    left: 0;
    z-index: 20;
    display: none;
    width: 300px;
    height: 100%;
    max-width: 86%;
    overflow: hidden;
    font-size: 14px;
    background: $container-background-color;

    &.animating,
    &.visible {
      display: block;
      transform: translateX(-100%);
      transition: transform 0.3s ease-in-out;
    }

    &.animating-out { transition-duration: 0.2s; }

    &.visible { transform: translateX(0); }
  }

  .header-tools {
    @extend %clearfix;
    position: relative;
    padding: em(14, 13) 19px;
    font-size: em(13);
    color: contrast($container-background-color, 50%);
    border-bottom: 1px solid contrast($container-background-color, 9%);
    transition: opacity 0.3s ease;

    a { color: mix($body-color, $container-background-color); }

    .subnav-active & { opacity: 0; }
  }

  .currency-selector-wrapper {
    position: relative;
    display: inline-block;
    margin-right: 14px;
    height: em(25, 10);
    max-width: 84px;
    padding: 5px 20px 4px 8px;
    font-size: em(12);
    border: 1px solid transparent;
    border-radius: 2px;

    &:not(.single-currency) {
      border-color: $border-color;
    }

    &::after {
      top: em(13, 12);
      right: em(5, 12);
    }
  }

  .customer-links {
    position: absolute;
    top: em(14, 14);
    right: 48px;

    a {
      display: inline-block;
      padding: 4px 2px;
      margin-right: 4px;
      vertical-align: middle;
    }
  }

  .mobile-menu-close {
    position: absolute;
    top: 13px;
    right: 11px;
    padding: 4px;
    font-size: 24px;
    font-weight: 300;
    line-height: 1;
  }


  // ------ Navigation ------ //

  .navigation {
    position: absolute;
    top: 58px;
    bottom: 0;
    width: 100%;
    overflow-y: auto;
    -webkit-overflow-scrolling: touch;
  }

  .nav-menu {
    position: absolute;
    width: 100%;
    transition: transform 0.3s ease;

    .subnav-active & {
      transform: translateX(-100%);
    }
  }

  // styling for top-level nav and subsequent generated nav elements
  .nav-menu-item,
  .subnav-block li {
    position: relative;
    border-bottom: 1px solid contrast($container-background-color, 9%);

    a {
      display: block;
      padding: 23px 20px 22px;
      font-size: em(16);
      line-height: 1;
      color: $body-color;
    }

    .caret { display: none; }

    > ul { display: none; }
  }

  .open-dropdown {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    width: 60px;
    height: 60px;
    color: $body-color;
    background: contrast($container-background-color, 2%);
    border-color: contrast($container-background-color, 6.5%);
    border-width: 0 0 0 1px;

    &:focus { outline: 0; }

    &:active { border: inherit; }

    .open-arrow {
      @include icon-arrow-right($body-color);
      position: relative;
      top: 2px;
      width: 16px;
      height: 16px;
    }
  }

  // container for dropdown menu
  .menu-dropdown {
    > a {
      font-weight: 600;
    }
  }

  // animation for dynamically injected subnav
  .subnav-block {
    position: absolute;
    top: 0;
    left: 0;
    display: none;
    width: 100%;
    height: 100%;
    transition: transform 0.3s ease;

    &.animating,
    &.visible {
      display: block;
    }

    &.animating-in { transform: translateX(100%); }
    &.animating-out { transform: translateX(-100%); }
    &.visible { transform: translateX(0); }

    .subnav-back & {
      &.animating-in { transform: translateX(-100%); }
      &.animating-out { transform: translateX(100%); }
      &.visible { transform: translateX(0); }
    }

    .subnav-dropdown {
      position: absolute;
      top: 62px;
      bottom: 0;
      // scss-lint:disable ImportantRule
      left: 0 !important; // override inline style attached by meganav JS
      // scss-lint:enable ImportantRule
      width: 100%;
      overflow-y: auto;
      -webkit-overflow-scrolling: touch;
    }
  }

  .subnav-heading {
    display: block;
    width: 100%;
    padding: 23px 20px 22px;
    font-size: em(16);
    font-weight: 600;
    line-height: 1;
    color: $body-color;
    text-align: left;
    background: contrast($container-background-color, 6.5%);
    border: 0;

    .subnav-back-arrow {
      display: inline-block;
      width: 11px;
      height: 11px;
      margin-right: 12px;
    }

    &:focus { outline: 0; }
  }

  // ------ Nav background overlay ------ //

  .menu-overlay {
    position: fixed;
    top: 0;
    right: 0;
    left: 0;
    z-index: 15;
    display: none;
    height: 100%;
    background: rgba($body-color, 0.4);

    &.animating,
    &.visible {
      display: block;
      opacity: 0;
      transition: opacity 0.2s ease;
    }

    &.visible { opacity: 1;  }
  }
}

/* scss-combine:general/meganav */
.mega-nav {
  @include media($min: $mobile-menu-breakpoint) {
    .menu-dropdown.tapped,
    .menu-dropdown:hover {
      .categories-dropdown {
        display: flex;
        overflow-y: auto;

        > .subnav-menu-item {
          padding: 0 30px 30px 0;
          margin-bottom: 0;
        }
      }
    }

    // when there are lots of nav items,
    // the nav is 100% width with a fluid grid
    .nav-width-maxed {
      flex-wrap: wrap;
      width: 100%;

      > .subnav-menu-item {
        flex: 0 0 auto;
        width: 25%;
      }
    }

    // when there are less nav items,
    // the nav fits the width of its contents
    .nav-width-not-maxed {
      flex-wrap: nowrap;

      > .subnav-menu-item {
        flex: 0 1 auto;
        min-width: 120px;
        margin-right: 30px;
      }
    }
  }

  // on larger screens, break to a width calculated based on quantity of items
  @include media($min: $large) {
    .nav-columns-4 {
      > .subnav-menu-item {
        width: 25%;
      }
    }

    .nav-columns-5 {
      > .subnav-menu-item {
        width: 20%;
      }
    }

    .nav-columns-6 {
      > .subnav-menu-item {
        width: percentage(1 / 6);
      }
    }
  }
}


/* scss-combine:general/footer */
// ------ newsletter subscription block ------ //

.section-subscription-form {
  padding: 52px 0 60px;
  border-top: 1px solid $border-color;

  .section-header { margin-bottom: 20px; }
}

.subscription-form {
  @include breakpoint(s-only) {
    .form-input,
    .button {
      width: 100%;
      margin-bottom: 8px;
    }
  }

  @include breakpoint(s-up) {
    max-width: rem(390);
    margin: 0 auto;

    // inputs are the only thing targeted via element which makes then difficult to override...
    // scss-lint:disable QualifyingElement
    input.form-input {
      float: left;
      width: rem(240);
      height: em(43);
    }

    .button {
      float: left;
      width: rem(132);
      padding-right: 0;
      padding-left: 0;
      margin-left: rem(8);
    }
  }

  .form-field {
    @extend %clearfix;
  }

  .button { height: em(43, 13); }
}

.subscription-summary {
  margin-top: 20px;
  text-align: center;
}

.main-footer {
  padding: 50px 0 40px;
  font-size: em(13);
  color: $footer-text-color;
  background: $footer-background-color;

  a {
    color: $footer-text-color;

    &:hover { color: $footer-header-color; }
  }

  p { margin-top: 0; }
}

.logo-column {
  @include breakpoint(m-only) {
    margin-bottom: $gutter * 2;
    text-align: center;
  }

  @include breakpoint(m-up) {
    @include fluid-width(280px, $max-width);
    float: left;
  }
}

.footer-columns-container {
  font-size: 0;
  text-align: center;

  @include breakpoint(m-up) {
    @include fluid-width(720px, $max-width);
    float: right;
    text-align: right;
  }
}

.footer-column {
  font-size: $font-size-base * (13 / 14); // need to reset to a px value here because the font-size context is 0
  text-align: center;

  @include breakpoint(s-only) {
    margin-bottom: $gutter * 2;
  }

  @include breakpoint(s-up) {
    @include fluid-width(326px, $max-width);
    display: inline-block;
    vertical-align: top;
  }

  @include breakpoint(m-up) {
    @include fluid-width(160px, 720);
    text-align: left;
  }
}

.footer-branding {
  .text {
    @extend %logo-text-treatment;
    color: $footer-text-color;

    a { color: inherit; }
  }

  .retina-logo & img {
    @include breakpoint(m-only) {
      max-height: 50px;
    }

    @include breakpoint(m-up) {
      max-width: 50%;
    }
  }
}

.footer-column-heading {
  margin: 0 0 10px;
  font-size: em(13, 13);
  font-weight: 700;
  color: $footer-header-color;
}

.footer-nav {
  li { margin-bottom: 4px; }
}

.footer-nav-view-all { font-size: em(11, 13); }


// ------ lower content bottom left ------ //

.footer-lower {
  @extend %clearfix;
  position: relative;
  clear: both;

  @include breakpoint(m-only) {
    text-align: center;
  }

  @include breakpoint(s-up) {
    margin-top: 30px;
  }
}

.footer-lower-left {
  @include breakpoint(m-up) { float: left; }

  a {
    text-decoration: none;

    &:hover { color: $footer-header-color; }
  }
}

.footer-site-info {
  display: inline-block;
  font-size: em(12, 13);
  color: rgba($footer-text-color, 0.9);

  &.hide-footer-site-info {
    display: none;
  }
}

.footer-copyright {
  margin-right: 16px;
}

.footer-credits {
  display: inline-block;
  font-size: em(11, 12);
}

.footer-lower-nav {
  margin-bottom: 8px;
}

.footer-lower-nav-item {
  display: inline-block;
  margin-right: 10px;

  a:hover {
    color: $footer-header-color;
  }
}

.payment-option-item {
  display: inline-block;
  margin: em(16) em(6) 0 0;
  color: mix($footer-background-color, $footer-text-color, 30%);

  svg {
    width: em(36);
    height: em(18);
  }
}

// ------ social media bottom right ------ //

.footer-social-media {
  font-size: 16px;
  line-height: 1;

  @include breakpoint(s-up) {
    padding-top: 26px;
  }

  @include breakpoint(m-only) {
    margin-bottom: $gutter * 2;
  }

  @include breakpoint(m-up) {
    position: absolute;
    right: 0;
    bottom: 0;
  }

  .social-media-link {
    display: inline-block;
    margin-left: 10px;
  }

  a {
    color: $footer-header-color;

    &:hover { color: $footer-text-color; }
  }

  .icon-social {
    width: 16px;
    height: 16px;
  }
}

/* scss-combine:general/banners */
.banners {
  @extend %clearfix;
  padding: 0 $gutter;
  color: $banner-text-color;
  text-align: center;
  background: $banner-background-color;
}

.banner {
  padding: 12px 0;
  font-size: em(13);
  text-align: center;
  border-bottom: 1px solid $banner-border-color;

  &:last-child { border-bottom: 0; }
}

// shipping message banners, full-width below header
.alert-banners {
  font-family: $heading-font-family;
  font-size: $body-font-size - 1;
  letter-spacing: em(1, 13);
  color: darken($success-color, 10%);
  text-transform: uppercase;
  background-color: rgba($success-color, 0.1);

  .banner { padding: 10px 0; }
}

/* scss-combine:general/breadcrumbs */
.breadcrumbs-container {
  max-width: $outer-width + ($gutter * 2);
  padding-right: $gutter;
  padding-left: $gutter;
  margin: 10px auto;
  font-size: em(11);

  @include breakpoint(m-up) {
    margin: 20px auto 0;
    font-size: em(13);
  }
}

.breadcrumbs {
  overflow-x: auto;
  white-space: nowrap;
  cursor: default;
}

.breadcrumb {
  position: relative;
  display: inline-block;
  padding-right: 1.3em;
  color: mix($body-color, $body-background-color);

  &:not(.is-active) {
    .breadcrumb-arrow {
      position: absolute;
      top: 0.25em;
      right: 0;
      display: block;
      width: 12px;
      height: 12px;
    }
  }

  .breadcrumb-arrow {
    display: none;
  }
}

.breadcrumb-label {
  &.link {
    color: $body-color;

    &:hover { color: contrast($body-color, 10%); }
  }
}

/* scss-combine:general/pagination */
.pagination-container {
  @extend %clearfix;
  margin-top: $gutter * 2;
  font-weight: 300;
  cursor: default;

  .pagination {
    @include breakpoint(s-only) { text-align: center; }

    @include breakpoint(s-up) { float: left; }
  }

  .pagination-item {
    display: inline-block;

    .page {
      @include breakpoint(s-only) {
        display: none;
      }
    }
  }

  .pagination-page {
    margin: 0 em(4);

    @include breakpoint(s-only) {
      display: none;
    }
  }

  .pagination-current {
    opacity: 0.8;
  }

  .to-top {
    float: right;

    @include breakpoint(s-only) {
      display: none;
    }
  }
}


/* scss-combine:general/errors */
.page-error {
  text-align: center;

  @include breakpoint(m-up) {
    padding-top: 120px;
    padding-bottom: 60px;
  }

  .page-heading {
    margin-bottom: 0;
  }
}

.page-error-message {
  font-size: em(16);
  color: $meta-color;
}

.page-error-search-form {
  display: block;
  max-width: 400px;
  margin: $gutter * 4 auto 0;

  .form-input-wrapper { right: 120px; }

  .button { width: 110px; }
}

/* scss-combine:general/quick-search */
$local-search-bg-color: $container-background-color;
$local-search-section-text-color: lighten($body-color, 50%);

$local-search-bg-select-color: lighten($body-background-color, 4%);
$local-search-bg-hover-color: darken($local-search-bg-select-color, 2%);

$local-search-text-color: $body-color;
$local-search-text-focus-color: mix($local-search-text-color, $local-search-bg-select-color);

$local-show-all-color: $link-color;
$local-show-all-focus-color: $link-hover-color;

.search-form-quick-search-wrapper {
  position: absolute;
  top: 100%;
  right: 0;
  z-index: 15;
  display: none;
  width: 420px;
  margin-top: 8px;
  background-color: $local-search-bg-color;
  box-shadow: rgba(0, 0, 0, 0.17) 0 2px 3px 0;

  &.quick-search-open {
    @include media($min: $mobile-menu-breakpoint) {
      display: block;
    }
  }

  .spinner {
    position: relative;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    display: block;
    width: 40px;
    height: 40px;
    margin: 30px auto;

    .pxu-fading-circle {
      width: 40px;
      height: 40px;
      margin-top: rem(-20);
      margin-left: rem(-20);
    }

    .pxu-circle::before {
      background-color: $local-search-text-color;
    }
  }
}

.quick-search-result-list {
  margin-bottom: 15px;

  &:last-child {
    margin-bottom: 0;
  }
}

.quick-search-section-title {
  margin: 10px 15px 7px;
  font-size: 12px;
  color: $local-search-section-text-color;

  &.empty-result {
    margin: 30px 15px;
    color: $local-search-text-color;
    text-align: center;
  }
}

.quick-search-result {
  position: relative;
  display: table;
  width: 100%;
  padding: 10px 0 8px;
  line-height: 17px;
  color: $local-search-text-color;
  white-space: nowrap;
  cursor: pointer;
  border-collapse: separate;
  border-spacing: 15px 0;
  table-layout: fixed;

  &.brand-result,
  &.category-result,
  &.content-result { padding: 7px 0 5px; }

  &.category-result {
    a {
      color: inherit;
    }
  }

  &.show-all-result {
    padding: 12px 15px;
    margin-right: auto;
    margin-left: auto;
    color: $local-show-all-color;
    text-align: center;
  }

  &.selected {
    background-color: $local-search-bg-select-color;
  }

  &:hover {
    background-color: $local-search-bg-hover-color;
  }

  .product-price {
    margin-bottom: 9px;
    font-size: em(16, 14);
    font-weight: $weight-light;
    // need this to come after because it will potentially override the font-weight above
    // scss-lint:disable DeclarationOrder
    @include optional-heading-font($price-use-heading-font);
  }

  .call-for-price { color: $meta-color; }

  .price-rrp {
    color: $meta-color;
    text-decoration: line-through;
  }

  .on-sale .price-value { color: $sale-text-color; }

  .tax-label {
    font-size: em(13, 16);
    font-weight: 300;
  }

  .price-second-line {
    display: inline-block;
    font-size: em(14, 16);
    color: $meta-color;
  }

  .price-you-save {
    font-family: $body-font-family;
    font-size: em(13, 16);
    letter-spacing: 0;
    color: $sale-text-color;
    text-transform: none;
  }

  .on-sale .list-price { color: $sale-text-color; }
}

.quick-search-result-image {
  display: table-cell;
  width: 50px;
  height: auto;
}

.quick-search-result-details {
  display: table-cell;
  width: auto;
  overflow: hidden;
  text-overflow: ellipsis;
  vertical-align: middle;
}

.quick-search-result-title {
  font-family: $heading-font-family;
  font-size: $heading-font-size-modifier * 1rem;
  font-weight: $heading-font-weight;
  color: $body-color;
}


// Pages
/* scss-combine:general/compare */
//----- popup compare tab on product lists -----//

.compare-tab {
  position: fixed;
  right: 40px;
  bottom: 0;
  z-index: 1;
  display: none;
  min-width: em(290, 13);
  padding: 9px 15px 12px;
  font-size: em(13);
  background: $container-background-color;
  border: 1px solid darken($body-background-color, 10%);
  box-shadow: 0 0 12px rgba(#000, 0.2);

  @include breakpoint(s-only) {
    right: 0;
    left: 0;
    border-width: 1px 0 0;
  }

  &.animating,
  &.visible {
    display: block;
    transform: translateY(100%);
    transition: transform 0.2s ease;
  }

  &.visible {
    transform: translateY(0);
  }
}

.compare-tab-upper {
  @extend %clearfix;
  margin-bottom: 17px;
  font-weight: 300;

  .item-limit { float: left; }

  .clear-compare {
    float: right;
  }
}

.compare-submit {
  position: absolute;
  right: 15px;
  bottom: 18px;
  height: 38px;
  font-size: em(11, 13);

  @include breakpoint(s-only) {
    bottom: 14px;
  }
}

.compare-items-list {
  margin-right: 120px;
}

.compare-tab-item {
  position: relative;
  display: inline-block;
  width: 50px;
  height: 50px;
  margin-right: 3px;
  text-align: center;

  @include breakpoint(s-only) {
    width: 40px;
    height: 40px;
  }
}

.compare-tab-item-remove {
  position: absolute;
  top: -4px;
  right: -4px;
  width: 14px;
  height: 14px;
  padding-bottom: 1px;
  font-size: em(13, 13);
  line-height: 1;
  color: $container-background-color;
  text-align: center;
  cursor: pointer;
  background: $body-color;
  border-radius: 50%;
  opacity: 0;

  &:hover {
    color: $container-background-color;
  }

  .compare-tab-item:hover & {
    opacity: 1;
  }
}

.compare-tab-item-image {
  img {
    max-height: 50px;

    @include breakpoint(s-only) {
      max-height: 40px;
    }
  }
}

//----- Compare page ----//

.compare-content {
  @include media($max: $compare-responsive-breakpoint) {
    overflow-x: auto;
  }
}

.header-column {
  @include media($max: $compare-responsive-breakpoint) {
    display: none;
  }

  @include media($min: $compare-responsive-breakpoint) {
    max-width: 115px;
  }
}

.compare-table {
  width: 100%;
  min-width: 900px;
  font-size: em(13);
  font-weight: 300;
  text-align: center;
  border-width: 0;
  table-layout: fixed;

  .button {
    width: 100%;
    max-width: 200px;
    padding: 11px 0 10px;
    margin: 10px 0;
    font-size: em(11, 13);
  }
}

.compare-empty-cell { border: 0; }

.compare-row {

  // alternate columns, after third column
  &:nth-child(odd):not(:first-child) {
    .compare-table-cell,
    .compare-table-heading {
      background: $table-stripe-background-color;
    }
  }
}

.compare-table-cell,
.compare-table-heading {
  padding: 10px 20px;
  border-top: 0;
  border-bottom: 0;
}

.compare-table-cell {
  min-width: 230px;

  // hide right border when header column is hidden
  @include media($max: $compare-responsive-breakpoint) {
    &:first-of-type {
      border-left: 0;
    }
  }
}

.compare-table-heading {
  font-family: $body-font-family;
  text-align: left;
  vertical-align: top;
}

.compare-remove {
  display: block;
}

.compare-description-row {
  text-align: left;
  vertical-align: top;
}

.compare-rating-row {
  .compare-no-rating {
    opacity: 0.6;
  }
}

/* scss-combine:general/search */
$local-tab-background-color: $search-tab-background-color;
$local-tab-border-color: $search-border-color;
$local-tab-border-active-color: $search-border-active-color;
$local-tab-border-active-color: $search-border-active-color;
$local-tab-anchor-active-color: $search-text-color;
$local-brand-category-bg-color: $search-background-color;

.search-head {
  margin-top: 30px;
  border-bottom: 1px solid $local-tab-border-active-color;
}

.search-tab-toggle {
  position: relative;
  bottom: -1px;
  display: inline-block;
  width: 155px;
  padding: 12px 20px;
  text-align: center;
  background-color: $local-tab-background-color;
  border: 1px solid $local-tab-border-color;
  border-bottom: 1px solid $local-tab-border-active-color;

  @include media($min: $mobile-menu-breakpoint) {
    width: auto;
    text-align: left;
  }

  &:first-child {
    margin-left: 5px;
  }

  &.disabled {
    display: none;
  }

  &.active {
    color: inherit;
    cursor: default;
    background-color: $container-background-color;
    border: 1px solid $local-tab-border-active-color;
    border-bottom: 1px solid $container-background-color;

    &:hover {
      color: $local-tab-anchor-active-color;
    }
  }
}

.search-tab {
  display: none;

  &.active {
    display: block;
  }

  .content-result {
    display: block;
    padding-bottom: 30px;
    margin-top: 30px;

    &:not(.last-item) {
      border-bottom: 1px solid $border-color;
    }
  }
}

.search-subhead {
  font-weight: 300;

  strong {font-weight: 900;}
}

.collection-brand-category-index-column {
  display: inline-block;
  width: 100%;
  padding: 0 20px;
  margin-bottom: 40px;
  background-color: $local-brand-category-bg-color;

}

.brand-results,
.category-results {
  h2 { margin-bottom: 5px; }
}

.category-results {
  margin-top: 10px;
  margin-bottom: 20px;
}

.brand-results {
  margin-top: 20px;
  margin-bottom: 20px;

  + .category-results { margin-top: 25px; }
}

.collection-content-index-column {
  margin-top: 80px;

  .content-search-tab & {
    float: none;
    margin-right: auto;
    margin-left: auto;
  }
}

.search-result {
  display: inline-block;

  &.category-result,
  &.brand-result {
    display: block;
  }
}

.content-result-title {
  a {
    font-family: $heading-font-family;
    font-size: $heading-font-size-modifier * 1.2rem;
    font-weight: $heading-font-weight;
    color: $headings-color;

    @include media($min: $mobile-menu-breakpoint) {
      font-size: $heading-font-size-modifier * 1.4rem;
    }
  }
}

.content-result-type {
  font-family: $meta-font;
  font-weight: $meta-weight;
  color: $meta-color;
}

.search-empty-notice {
  text-align: center;
  padding: ($gutter * 4) $gutter ($gutter * 6);
}

.search-empty-notice-heading {
  @extend %heading-medium;
}

/* scss-combine:general/contact */
.button-contact-submit {
  @extend .button-primary;
}

/* scss-combine:general/sitemap */
.sitemap {
  @extend %clearfix;
}

.sitemap-group {
  margin-bottom: em($gutter * 2);

  @include breakpoint(s-up) {
    float: left;
    width: 50%;
    padding-left: em($gutter);
  }

  @include breakpoint(m-up) {
    float: left;
    width: percentage(1 / 3);
  }
}

.sitemap-show-all {
  margin-top: em(2, 12);
  font-size: rem(12);
  list-style: none;
}

/* scss-combine:general/gift-certificates */
.giftcard-nav {
  padding: em(40) 0 em(34);
  margin-bottom: em(26);
}

.giftcard-nav-item {
  padding: 0 0 em(7, 12);
  border-bottom: 1px solid transparent;

  &.is-active {
    color: $body-color;
    cursor: default;
  }
}

.giftcard-heading-container {
  margin-bottom: em(55);
}

.giftcard-heading {
  @extend %heading-medium;
  margin-bottom: em(12, 30);
}

.giftcard-subheading {
  margin: 0;
  font-weight: 300;
}

.giftcard-redeem-steps {
  @extend .rte;
  padding-left: em(24);
}


// Blog
/* scss-combine:blog/blog-index */
.blog-items-list {
  @extend %clearfix;
  @include row(1020, 30);
}


.blog-item {
  margin-bottom: 40px;

  @include breakpoint(m-up) {
    @include fluid-width(354, 738, 30);
    float: left;

    &:nth-child(2n + 1) {
      clear: left;
    }
  }

  @include breakpoint(l-up) {
    @include fluid-width(320, 1020, 30);

    // reset rule from above :/
    &:nth-child(2n + 1) {
      clear: none;
    }

    &:nth-child(3n + 1) {
      clear: left;
    }
  }

  // hide blog image spaceholder for single-column grids
  @include breakpoint(m-only) {
    .blog-image-container-empty {
      display: none;
    }
  }

  .blog-image-container {
    @include maintain-ratio(2 1);
    position: relative;
    margin-bottom: 24px;
    overflow: hidden;

    img {
      @include centerer;
      width: 100%;
    }
  }

  .timestamp {
    font-size: em(13);
    font-weight: 300;
    color: mix($body-color, $container-background-color);
  }

  .title {
    margin: 6px 0 16px;
    font-size: rem($body-font-size * 1.2);

    @include breakpoint(s-up) {
      font-size: rem($body-font-size * 1.4);
    }

    @if $body-font-family == "Fira Sans" {
      font-family: $heading-font-family;
    } @else {
      font-family: $body-font-family;
    }

    font-weight: $heading-font-weight;
    color: $headings-color;
  }

  .excerpt {
    margin-bottom: 15px;
    font-weight: 300;
    color: $body-color;
  }

  .continue-reading {
    font-weight: 300;

    &:hover {
      text-decoration: underline;
    }
  }
}


// BLOG LISTING
.blog-post-index {

  // override .page-container styles to account for hefty margin-bottom on blog grid items
  @include breakpoint(l-up) {
    margin-bottom: 20px;
  }

  .page-heading { text-align: center; }

  .blog-item {
    margin-bottom: 40px;

    @include breakpoint(m-up) {
      margin-bottom: 80px;
    }
  }
}

/* scss-combine:blog/blog-single */
.blog-post-single {
  // override .page-container styles to account for social buttons at bottom
  @include breakpoint(l-up) {
    margin-bottom: 80px;
  }
}


.blog-post-image-container {
  img {
    width: 100%; // TODO: temporary until image API is settled
  }
}

.blog-post-header {
  margin: 52px 0 40px;
  text-align: center;
}

.blog-post-meta {
  font-size: em(13);
  font-weight: $meta-weight;
  color: $meta-color;

  a {
    color: inherit;

    &:hover { color: $link-color; }
  }
}

.blog-post-tags {
  display: inline-block;
}

.blog-post-title {
  @extend %heading-large;
  margin: 16px 0 18px;
  font-size: em(24);

  @include breakpoint(s-up) {
    font-size: em(30);
  }

  @include breakpoint(m-up) {
    font-size: em(36);
  }
}

.post-author {
  font-size: em(13);

  @include breakpoint(s-up) {
    font-size: em(14);
  }
}

.blog-post-share {
  margin-top: 65px;
  text-align: center;

  .heading { margin: 0 0 6px; }

  .share-link {
    margin: 10px 6px;
  }

  a {
    @include breakpoint(m-up) {
      font-size: em(24);
    }

    &:hover {
      color: $accent-color;
    }
  }

  .share-icon {
    width: 18px;
    height: 18px;
  }
}



// Collection
/* scss-combine:collection/collection-common */
$local-sidebar-column-desktop: 230;
$local-sidebar-column-tablet: 300;

$local-products-column-desktop: 760;
$local-products-column-tablet: 690;

.collection-columns {
  margin-top: 58px;

  @include breakpoint(m-up) {
    @include row(1020, 30);
  }
}

.collection-progress-overlay {
  @extend %fill-parent;
  display: none;
  background-color: rgba($container-background-color, 0.9);

  &.visible {
    display: block;
  }

  .inner {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 60px;
    padding: 90px 0 0 6px;
    margin-top: -30px;
    margin-left: -30px;
    font-size: em(12);
    font-style: italic;
    font-weight: 300;
    color: rgba($meta-color, 0.6);
    text-align: center;
  }

  .pxu-fading-circle {
    top: 0;
    left: 0;
    margin: 0;
  }
}

.collection-name {
  @extend %heading-large;
  margin-top: 0;
  margin-bottom: 0;

  @include breakpoint(m-only) {
    margin-bottom: $gutter;
    text-align: center;
  }
}

.collection-sidebar-column {
  position: relative;
  clear: left;

  @include breakpoint(m-only) {
    text-align: center;
  }

  @include breakpoint(m-up) {
    @include fluid-width($local-sidebar-column-tablet, 1020, 30);
    float: left;
  }

  @include breakpoint(l-up) {
    @include fluid-width($local-sidebar-column-desktop, 1020, 30);
  }

  .collection-progress-overlay {
    background-color: rgba(#fff, 0.4);
  }
}

.collection-product-index-column,
.collection-content-index-column,
.collection-brand-category-index-column {
  position: relative;
  margin-bottom: $gutter * 2;

  @include breakpoint(m-up) {
    @include fluid-width($local-products-column-tablet, 1020, 30);
    float: right;
  }

  @include breakpoint(l-up) {
    @include fluid-width($local-products-column-desktop, 1020, 30);
  }
}

.category-image-container {
  margin-bottom: 30px;

  @include breakpoint(m-up) {
    margin-bottom: 52px;
  }
}

.category-description {
  margin-bottom: $gutter;
  font-size: em(13);

  @include breakpoint(m-only) {
    max-width: 500px;
    margin-right: auto;
    margin-left: auto;
    text-align: center;
  }

  @include breakpoint(m-up) {
    margin-bottom: $gutter * 3;
  }
}

.sidebar-block {
  padding-top: em(32);
  margin-top: em(20);
  border-top: 1px solid $border-color;

  @include breakpoint(m-only) {
    margin-bottom: $gutter * 2;
  }

  .is-active {
    color: $body-color;
  }
}

.sidebar-heading {
  margin: 0 0 13px;
  font-weight: 700;
}

.collection-sorting {
  @extend %clearfix;

  @include breakpoint(m-only) {
    text-align: center;
  }
}

.product-sort-select {
  font-weight: 300;

  @include breakpoint(m-up) {
    float: right;
  }

  .form-label {
    display: inline-block;
    color: $meta-color;
  }

  .sort-select-input-wrapper {
    display: inline-block;

    &:after {
      right: em(4, 11);
    }
  }

  // this class is added to the <body>
  .ajax-progress & {
    pointer-events: none;
    opacity: 0.8;
  }
}

.product-empty-notice {
  padding: $gutter * 4 $gutter;
  font-size: em(13);
  font-style: italic;
  color: mix($meta-color, $container-background-color);
  text-align: center;
}

/* scss-combine:collection/faceted-search */
.button-toggle-facets {
  display: none;

  @include breakpoint(m-only) {
    display: block;
    width: 50%;
    margin: 0 auto $gutter;
  }

}

.faceted-search {
  font-weight: 300;
  text-align: left;

  @include breakpoint(m-only) {
    display: none;
    margin-bottom: $gutter;
    border: 1px solid $border-color;
    border-radius: 3px;

    &.filters-visible { display: block; }
  }

  @include breakpoint(m-up) {
    margin-top: $gutter;
  }
}

.faceted-search-filter {
  @include breakpoint(m-up) {
    border-top: 1px solid $border-color;

    &:last-child { border-bottom: 1px solid $border-color; }
  }

  @include breakpoint(m-only) {
    padding: 0 14px;
    border-bottom: 1px solid $border-color;

    &:last-child { border-bottom: 0; }
  }
}

.facet-section-header {
  position: relative;
  width: 100%;
  padding: 17px 0 14px;
  line-height: 1;
  text-align: left;
  background: none;
  border: 0;

  &:focus { outline: 0; }
}

.facet-section-toggle-icon {
  position: absolute;
  top: 17px;
  right: 0;
  width: 12px;
  height: 12px;

  &:before,
  &:after {
    position: absolute;
    display: block;
    background: $headings-color;
    content: "";
  }

  &:before {
    top: 0;
    left: 5px;
    width: 2px;
    height: 12px;
    transition: all 0.1s ease;

    .is-open & { display: none; }
  }

  &:after {
    top: 5px;
    left: 0;
    width: 12px;
    height: 2px;
  }
}

.facet-section-title {
  margin: 0;
  font-weight: $weight-bold;
}

.faceted-search-filter-list {
  display: none;
  margin: 0 0 14px;

  .is-open & { display: block; }
}

.faceted-search-item {
  @extend %clearfix;

  a {
    position: relative;
    display: block;
    padding: em(5) 0 em(5) 20px;
    line-height: 1;
  }

  .facet-checkbox {
    position: absolute;
    top: 50%;
    left: 0;
    margin-top: em(-7);
  }

  .faceted-search-item-label {
    @extend %ellipsis;
    position: relative;
    display: inline-block;
    max-width: 100%;
    padding-right: 40px;
    line-height: inherit;
    vertical-align: middle;
  }

  .faceted-search-item-count {
    position: absolute;
    right: 0;
    bottom: 0;
    width: 37px;
    color: mix($body-color, $body-background-color);
  }
}

.facet-filter-wrapper {
  display: none;

  .is-open & {
    display: block;
  }
}

.faceted-show-more-button {
  display: block;
  padding-bottom: 10px;
  font-weight: 700;

  .toggle-link-text-on {
    display: none;
  }
}

// price range

.facet-range-input-group {
  @extend %clearfix;
  position: relative;
  padding-right: 80px;
}

.facet-range-inputs {
  .form-field {
    float: left;
    width: 48%;

    &:last-child { float: right; }
  }

  .form-input { height: em(50); }
}

.facet-submit {
  position: absolute;
  top: 0;
  right: 0;
  width: 70px;
  height: em(50, 13);
  padding-right: 0;
  padding-left: 0;
  font-family: $body-font-family;
  font-size: em(13);
  letter-spacing: 0;
  text-transform: none;
}


// star rating
.facet-rating {
  .star {
    font-size: 12px;
    color: $border-color;

    &.checked { color: $link-color; }
  }
}

.facet-selected-filters {
  padding-top: 26px;

  @include breakpoint(m-only) {
    padding-bottom: 26px;
    text-align: center;
  }

  @include breakpoint(m-up) {
    margin-top: 20px;
    border-top: 1px solid $border-color;
  }

  .clear-all {
    display: inline-block;
    padding: 2px;
    font-size: em(12);
    color: $link-color;
  }
}

.selected-filter-items {
  @include breakpoint(m-up) {
    max-width: 153px;
    margin-bottom: 8px;
  }
}

.selected-filter-item {
  margin-bottom: 1px;

  @include breakpoint(m-only) {
    display: inline-block;
  }
}

.selected-filter-item-link {
  @extend %ellipsis;
  position: relative;
  display: block;
  padding: 5px 22px 6px 10px;
  font-size: em(13);
  font-weight: 300;
  color: $body-color;
  background: mix($body-background-color, $container-background-color);


  @include breakpoint(m-only) {
    padding-right: 26px;
    border-radius: 4px;
  }

  @include breakpoint(m-up) {
    li:first-child > & {
      border-top-right-radius: 4px;
      border-top-left-radius: 4px;
    }

    li:last-child > & {
      border-bottom-right-radius: 4px;
      border-bottom-left-radius: 4px;
    }
  }

  .remove {
    position: absolute;
    top: 8px;
    right: 10px;
    font-size: em(15, 13);
    line-height: 1;
    color: darken(mix($body-background-color, $container-background-color), 10%);
  }

  &:hover {
    color: inherit;
    background: darken(mix($body-background-color, $container-background-color), 2%);
  }
}


/* scss-combine:collection/brands */
.brand-items-list {
  @extend %clearfix;
  @include row(1024, 8);
}

.brand-item {
  @include fluid-width(200, 408, 8); // 2 column
  display: block;
  float: left;
  padding: $gutter 0;
  margin-bottom: $gutter;
  text-align: center;

  // scss-lint:disable DeclarationOrder
  &:nth-child(2n + 1) { clear: left; }

  @include breakpoint(s-up) {
    @include fluid-width(200, 616, 8); // 3 column

    &:nth-child(2n + 1) { clear: none; }
    &:nth-child(3n + 1) { clear: left; }
  }

  @include breakpoint(l-up) {
    @include fluid-width(250, 1024, 8); // 4 column

    &:nth-child(3n + 1) { clear: none; }
    &:nth-child(4n + 1) { clear: left; }
  }

  a {
    display: block;
    color: $body-color;

    &:hover {
      color: $accent-color;
    }
  }
}

.brand-item-image-container {
  .no-brand-image & {
    @include maintain-ratio(25 8);
    max-width: 200px;
  }
}

.brand-item-title {
  @extend %heading-small;
  margin-bottom: 0;
  color: inherit;
}


// Product
/* scss-combine:product/product-grid */
.product-grid-container {
  @extend %clearfix;
  @include row(1024, 8);
}

.product-item {
  position: relative;
  padding-bottom: 16px;
  margin-bottom: 16px;
  text-align: center;
  border: 1px solid transparent;
  transition: border-color 0.2s ease, box-shadow 0.2s ease;

  @include media($min: 320px) {
    @include fluid-width(200, 408, 8); // 2 column
    float: left;

    &:nth-child(2n + 1) { clear: left; }
  }

  @include media($min: 540px) {
    @include fluid-width(200, 616, 8); // 3 column

    &:nth-child(2n + 1) { clear: none; }
    &:nth-child(3n + 1) { clear: left; }
  }

  &.four-across {
    @include breakpoint(l-up) {
      @include fluid-width(250, 1024, 8); // 4 column

      &:nth-child(3n + 1) { clear: none; }
      &:nth-child(4n + 1) { clear: left; }
    }
  }

  &:hover {
    border: 1px solid $border-color;
    box-shadow: 3px 3px 5px rgba(0,0,0,0.05);
  }
}

.product-item-sale-tag {
  position: absolute;
  z-index: 1;
  font-size: em(10);
  font-weight: 800;
  line-height: 1;
  color: $container-background-color;
  text-transform: uppercase;
  background-color: $sale-badge-color;

  .sale-tag-rectangle & {
    top: 10px;
    right: 10px;
    padding: em(7, 10) em(9, 10) em(6, 10);
  }

  .sale-tag-circle & {
    top: 16px;
    right: 16px;
    width: em(36, 10);
    height: em(36, 10);
    padding-top: em(14, 10);
    text-align: center;
    border-radius: 50%;
  }
}

.product-item-sold-out-tag {
  position: absolute;
  z-index: 1;
  font-size: em(10);
  font-weight: 800;
  line-height: 1;
  color: $container-background-color;
  text-transform: uppercase;
  background-color: mix($body-color, $container-background-color);
  top: 10px;
  right: 10px;
  padding: em(7, 10) em(9, 10) em(6, 10);
}

.product-item-thumbnail {
  position: relative;
  padding: 10px;
  margin-bottom: 10px;

  .product-grid-aspect-three-to-one & {
    @include maintain-ratio(3 1);
  }

  .product-grid-aspect-five-to-two & {
    @include maintain-ratio(5 2);
  }

  .product-grid-aspect-two-to-one & {
    @include maintain-ratio(2 1);
  }

  .product-grid-aspect-five-to-three & {
    @include maintain-ratio(5 3);
  }

  .product-grid-has-aspect & {
    background-position: center;
    background-repeat: none;
    background-size: cover;
  }
}

.product-item-thumbnail img {
  .product-grid-has-aspect & {
    @extend .show-for-sr;
  }
}

.product-item-details { padding: 0 16px; }

.product-item-title {
  margin: 10px 0 2px;
  font-weight: 700;
  color: $headings-color;
}

.product-item-color-options {
  display: inline-block;

  li {
    float: left;
    margin: 0 2px;
  }
}

// not supported at present
.product-item-swatch-wrap {
  display: block;
  cursor: pointer;
  border: 1px solid $container-background-color;

  .swatch {
    display: block;
    width: 20px;
    height: 20px;
    border: 1px solid transparent;
  }

  &:hover,
  &.active {
    .swatch { border: 1px solid $container-background-color; }
  }

  &:hover { border-color: rgba($body-color, 0.2); }
  &.active { border-color: $body-color; }
}

.product-item-price {
  @include optional-heading-font($price-use-heading-font);
  font-weight: 300;
  color: $body-color;

  .on-sale .price-value { color: $sale-text-color; }

  .price-rrp {
    color: $meta-color;
    text-decoration: line-through;
  }

  .tax-label { font-size: em(11, 14); }
}

.product-item-rating {
  margin-top: 3px;

  // hide empty ratings
  &.no-rating { visibility: hidden; }
}

.product-item-compare {
  margin-top: 7px;
  font-size: em(13);
  color: $link-color;

  .input-checkbox {
    &:focus {
      outline: 0;
    }
  }

  .form-label-text { display: inline-block; }

  &:hover {
    .input-checkbox,
    .form-label-text {
      cursor: pointer;
    }
  }

  .no-js & {
    visibility: hidden;
  }
}

// tweaks for smaller grids on product page
.grid-small {
  .product-item {
    // force 4-col grid at all times
    @include fluid-width(250, 1024, 8);
    clear: none;
  }

  .product-item-title { font-size: em(13); }

  .product-item-price { font-size: em(13); }
}

.quick-shop {
  position: absolute;
  top: 48%;
  left: 50%;
  display: block;
  width: 110px;
  padding: 5px 0;
  margin-left: -55px;
  font-size: em(13);
  background: $container-background-color;
  border-radius: 3px;
  opacity: 0;
  transform: translateY(10px);
  box-shadow: 0 1px 2px rgba(#000, 0.2);
  transition: opacity 0.2s ease, transform 0.2s ease;

  .js .product-item:hover & {
    opacity: 1;
    transform: translateY(0);
    transition-delay: 0.1s;
  }

  &:hover {
    color: $container-background-color;
    background-color: $accent-color;
  }

  // hide on touch screens. Only desktops get quick view
  .has-touch & { display: none; }
}

/* scss-combine:product/product-single */
.container.single-product {
  @include breakpoint(s-up) { padding-top: 40px; }

  @include breakpoint(m-up) {
    padding-top: 54px;

    // scaffold out our layout for the product page
    @include breakpoint(m-up) {
      .product-images-column,
      .product-meta-column {
        @include fluid-width(679, 1020, 0);
        float: right;
      }

      .product-info-column {
        @include fluid-width(300, 1020, 0);
        float: left;
      }
    }
  }
}


//----- main product info & options in sidebar -----//

.product-info-column {
  @include breakpoint(m-only) {
    margin-bottom: $gutter * 2;
  }

  .cart-messages-upper .alert { margin-bottom: 1.5em; }

  .product-title {
    @extend %heading-large;
    margin: 0 0 rem(18);

    @if $heading-font-family == "Pathway Gothic One" {
      line-height: 1;
    } @else {
      line-height: 1.35;
    }
  }

  .product-brand {
    display: inline-block;
    margin-bottom: (6em / 18);
    font-size: em(20);
    color: $meta-color;
  }

  .product-price {
    margin-bottom: 9px;
    font-size: em(16, 14);
    font-weight: $weight-light;
    // need this to come after because it will potentially override the font-weight above
    // scss-lint:disable DeclarationOrder
    @include optional-heading-font($price-use-heading-font);
  }

  .call-for-price { color: $meta-color; }

  .price-rrp {
    color: $meta-color;
    text-decoration: line-through;
  }

  .on-sale .price-value { color: $sale-text-color; }

  .tax-label {
    font-size: em(13, 16);
    font-weight: 300;
  }

  .price-second-line {
    display: inline-block;
    font-size: em(14, 16);
    color: $meta-color;
  }

  .price-you-save {
    font-family: $body-font-family;
    font-size: em(13, 16);
    letter-spacing: 0;
    color: $sale-text-color;
    text-transform: none;
  }

  .on-sale .list-price { color: $sale-text-color; }

  .product-quantity-container {
    margin-bottom: 22px;

    .form-input {
      width: 60px;
      height: 32px;
      padding-top: 0;
      padding-bottom: 0;
    }
  }

  hr { margin: 24px 0; }

  .button.add-to-cart {
    margin-top: 5px;
    margin-bottom: 8px;
  }

  .button.add-to-wishlist {
    font-family: $body-font-family;
    font-size: em(11);
  }
}

.product-details-block {
  @extend %clearfix;
  margin: $gutter 0;
  font-size: em(13);
}

.product-details-heading {
  font-size: 1em;
  margin: 0 0 em(4);
  font-weight: 600;
}

.product-details-name,
.product-details-value {
  float: left;
  margin: 0;
}

.product-details-name {
  margin-right: 6px;
  clear: left;
  color: $meta-color;
}

.product-details-hidden { display: none; }

.product-purchase-message-container {
  display: none;

  &.visible { display: block; }
}

.product-quantity-submit-columns {
  .alert {
    padding: 10px 15px;
    margin-bottom: 8px;
  }
}

.single-product-share {
  margin-top: em(24);

  a {
    font-size: em(16);
    color: mix($body-color, $container-background-color, 30%);

    &:hover { color: mix($body-color, $container-background-color, 46%); }
  }
}




//----- Product variation image preview -----//

.variation-preview {
  position: relative;
  display: block;
  height: em(50);
  padding: 0 em(14) 0 em(50);
  margin-top: em(20);
  color: $meta-color;
  border: 1px solid $border-color;
  border-radius: 3px;

  &:hover {
    color: contrast($meta-color, 10%);
    border-color: contrast($border-color, 10%);
  }
}

.variation-preview-thumb-wrap {
  position: absolute;
  top: em(6);
  left: em(6);
}

.variation-preview-thumb {
  width: em(36);
  height: em(36);
  background-position: center center;
  background-size: cover;
}

.variation-preview-label {
  display: inline-block;
  padding: em(15, 13) 0 em(7, 13);
  font-size: em(13);

  .variation-preview:hover & {

  }
}

//----- product images -----//

.product-images-container {
  position: relative;
  margin-bottom: 40px;

  @include breakpoint(m-only) {
    padding-bottom: 20px;
    margin: 20px 0;
  }
}

.product-slides-container {
  position: relative;
  overflow: hidden;

  @include breakpoint(m-only) {
    margin-right: -$gutter;
    margin-left: -$gutter;

    &:after {
      display: none;
      content: "swipe";
    }
  }
}

.product-slide {
  position: relative;
  float: left;
  width: 100%;
  text-align: center;

  @include breakpoint(m-up) {
    position: absolute;
    top: 0;
    left: 0;
    z-index: -1;
    opacity: 0;
    transition: all 0.2s ease;

    &.current {
      z-index: auto;
      opacity: 1;
    }

    // only show the first image when js is disabled
    .no-js &:first-child {
      position: static;
      opacity: 1;
    }
  }

  @include breakpoint(m-only) {
    .swipe-fade-active & { transition: all 0.2s ease; }

    .no-js &:not(:first-child) { display: none; }
  }

  img { max-height: 582px; }
}

.product-slides-wrap {
  position: relative;
  overflow: hidden;
  transition: height 0.2s ease;
}

.product-images-pagination {
  padding: 0;
  margin: 0;
  text-align: center;

  // images along bottom
  @include breakpoint(m-up) {
    margin-top: $gutter * 2;

    li {
      display: inline-block;
      margin: 0 3px 3px 0;
      background: $container-background-color;
      border: 1px solid $container-background-color;
      transition: border 0.2s ease;

      &.active {
        border: 1px solid $border-color;
      }

      a {
        display: block;
        padding: 3px;
      }

      img { height: 60px; }
    }
  }

  // pagination dots
  @include breakpoint(m-only) {
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;

    li {
      display: inline-block;
      margin: 0 1px;
      vertical-align: bottom;
      background: $border-color;
      border-radius: 50%;

      &.active { background: $body-color; }

      a {
        display: block;
        width: 7px;
        height: 7px;
      }

      img { display: none; }
    }
  }

  // hide pagination if JS is disabled
  .no-js & { display: none; }
}


//----- product sections -----//

.product-meta {
  margin-bottom: $gutter * 2;

  @include breakpoint(m-up) {
    margin-bottom: 64px;
  }
}

.product-section-header {
  padding-bottom: 6px;
  margin-bottom: 30px;
  border-bottom: 1px solid $border-color;
}

.product-section-title {
  @extend %heading-small;
  margin: 0 0 6px;
  color: $headings-color;
}

//----- tabs -----//

// hide tab headings by default. If the tabs JS has initialized, show them.
.product-tab-links {
  display: none;
  margin-bottom: 46px;
  border-bottom: 1px solid $border-color;

  .tabs-active & { display: block; }
}

.product-tab-link {
  display: inline-block;
  margin: 0 32px -1px 0;

  a {
    display: block;
    padding: 6px 0 11px;
    font-weight: 300;
    color: $meta-color;
    border-bottom: 2px solid transparent;
  }

  &:hover {
    a {
      border-bottom-color: $border-color;
    }
  }

  &.active {
    a {
      font-weight: 600;
      color: $body-color;
      border-bottom-color: $accent-color;
    }
  }
}

// if tabs JS hasn't initialized (only one tab block) show the tab block
.tabs-inactive {
  .tab-content-panel { display: block; }
}

.product-videos-list {
  .video-container {
    margin-bottom: 2em;
  }
}

//----- related & customers-viewed section -----/

.product-grid-scroll-container {
  overflow: auto;

  // max four items.
  .product-grid-container {

    // Grid shows two with scrollbar on mobile
    width: 200%;

    // three with scrollbar on tablet
    @include breakpoint(s-up) { width: 133.33%; }

    // four as usual with large screens
    @include breakpoint(l-up) { width: 100%; }
  }
}

/* scss-combine:product/product-options */
.product-options-container {
  margin: 24px 0;
  border-bottom: 1px solid $border-color;

  .form-field-title {
    margin-bottom: 6px;
    font-weight: 600;
  }

  .form-field {
    margin-bottom: 22px;

    &.#{$all-text-inputs} {
      width: 100%;
    }

    .input-number { max-width: 25%; }

    .input-select { width: 100%; }

    textarea {
      height: auto;
      resize: vertical;
    }
  }
}

.option-unavailable {
  opacity: 0.5;
}

// ---------------------------------------------------------------- */
// Swatch: Set of options displaying color / pattern choices

.swatch-value {
  font-weight: normal;
}

.form-field-swatch {
  .form-field-control {
    @extend %clearfix;
  }

  .form-label-text {
    @extend .show-for-sr;
  }

  .swatch-wrap {
    position: relative;
    float: left;
    margin: 0 2px 2px 0;
    border: 1px solid transparent;

    // ensure hovered swatches don't extend offscreen on smaller devices
    @include breakpoint(s-only) {
      &:first-child .swatch-pattern-expanded {
        left: -10px;
        transform: translateX(0);
      }

      &:last-child .swatch-pattern-expanded {
        left: auto;
        right: -10px;
        transform: translateX(0);
      }
    }

    //override swatch border-color in core/_swatch.scss file
    &.swatch-none {
      .swatch {
        border-color: transparent;
      }

      &:hover .swatch {
        border-color: $border-color;
      }

      .swatch-radio {
        &:checked + .swatch {
          border-color: contrast($border-color, 10%);
        }
      }
    }
  }

  // The block that the swatch sits within.
  .swatch {
    position: relative;
    display: block;
    width: 32px;
    height: 32px;
    border: 1px solid transparent;

    svg {
      width: 26px;
      height: 26px;
      box-shadow: 0 0 0 1px rgba(#000, 0.1) inset;
    }
  }

  .swatch-wrap:hover .swatch {
    border-color: $border-color;
  }

  // Blocks displaying the pattern / colors
  .swatch-color {
    position: absolute;
    top: 2px;
    right: 2px;
    bottom: 2px;
    left: 2px;

    &.secondary {
      left: 50%;
    }

    &.tertiary {
      top: 50%;
      left: 50%;
    }
  }

  .swatch-radio {
    position: absolute;
    z-index: 1;
    width: 100%;
    height: 100%;
    cursor: pointer;
    opacity: 0;

    // Selected Swatch Item
    &:checked + .swatch {
      border-color: contrast($border-color, 10%);
    }
  }

  .swatch-pattern-expanded {
    display: none;
    position: absolute;
    bottom: calc(100% + 10px);
    left: 50%;
    padding: 3px;
    border: 1px solid $border-color;
    border-radius: 3px;
    background: $container-background-color;
    transform: translateX(-50%);

    img {
      display: block;
      width: 100px;
      max-width: none;
    }
  }

  .swatch-wrap:hover .swatch-pattern-expanded {
    display: block;
  }
}


// ---------------------------------------------------------------- */
// File Upload

.form-file-types,
.form-file-size {
  display: block;
  margin-top: em(3, 9);
  font-size: em(9);
  color: $meta-color;
}


// ---------------------------------------------------------------- */
// Rectangles - Form options displayed as a series of rectangles with text

.form-field-rectangle {
  @extend %clearfix;

  .rectangle {
    position: relative;
    display: inline-block;
    float: left;
    margin: 0 5px 5px 0;

    &:hover .rectangle-text {
      border-color: contrast($border-color, 10%);
    }
  }

  .rectangle-text {
    display: block;
    min-width: em(40, 11);
    padding: em(10, 11);
    margin: 0 em(6) 0 0;
    font-family: $heading-font-family;
    font-size: em(11);
    font-weight: 600;
    line-height: 1;
    text-align: center;
    border: 1px solid $border-color;
  }

  .form-input {
    position: absolute;
    z-index: 1;
    width: 100%;
    height: 100%;
    cursor: pointer;
    opacity: 0;

    &:checked + .rectangle-text {
      color: $accent-color;
      border-color: $accent-color;
    }
  }
}


// ---------------------------------------------------------------- */
// Pick-Product

.product-picklist-item {
  position: relative;
  display: block;
  margin-bottom: -1px;
}

.product-picklist-radio {
  position: absolute;
  top: 50%;
  left: 14px;
  margin-top: -7px;
  line-height: 14px;
}

.product-picklist-item-body {
  position: relative;
  display: table;
  width: 100%;
  min-height: 60px;
  padding: 6px 8px 6px 84px;
  font-size: em(13);
  cursor: pointer;
  border: 1px solid transparent;
  border-radius: 3px;

  &.picklist-none { padding-left: 36px; }

  &:hover {
    z-index: 1;
    border-color: contrast($body-background-color, 6%);
  }

  [type="radio"]:checked ~ & {
    z-index: 2;
    color: $link-color;
    border-color: $link-color;
  }
}

.product-picklist-image-wrap {
  position: absolute;
  top: 8px;
  bottom: 8px;
  left: 34px;
  width: 40px;
  overflow: hidden;
}

.product-picklist-image {
  @include centerer;
}

.product-picklist-label-text {
  display: table-cell;
  line-height: 1.4;
  color: inherit;
  vertical-align: middle;
}

// ---------------------------------------------------------------- */
// Event input

.product-event-bounds {
  clear: left;
  display: block;
  margin-top: em(4, 10);
  color: $meta-color;
  font-size: em(10);
}

/* scss-combine:product/product-reviews */
// hide element on page
.review-modal-container {
  display: none;
}

.modal-leave-review {
  @include breakpoint(s-up) {
    max-width: 1060px;
    padding: 20px;

    .modal-content {
      padding: 50px;
    }
  }

  .modal-body {
    @extend %clearfix;
    @include row(910, 30);
  }
}


.review-image-column {
  @include fluid-width(430, 920, 30);
  float: left;
  border-right: 1px solid $border-color;

  @include breakpoint(m-only) {
    display: none;
  }
}

.review-image-container {
  padding-right: 30px;
  text-align: center;
}

.review-image-caption {
  @extend %heading-small;
  margin-top: 40px;
}

.review-form-column {
  @include breakpoint(m-up) {
    @include fluid-width(460, 920, 30);
    float: left;
  }
}

.review-form-heading {
  @extend %heading-large;
  margin: 3px 0 30px;
}

// clickable stars
.form-field-input-stars {
  display: inline-block;
  vertical-align: middle;
  padding: 0;
  margin: 0 0 0 3px;
  font-size: em(20);
  border: 0;

  &.form-field-error {
    position: relative;
    padding-bottom: 24px;
  }

  .star {
    float: right;
    padding-right: 4px;
    line-height: 1;
    color: rgba($body-color, 0.2);
    cursor: pointer;

    &:hover,
    &:hover ~ .star {
      color: $accent-color;
    }

    .icon-star {
      display: inline-block;
      width: 19px;
      height: 19px;
      fill: currentColor;
    }
  }

  input:checked {
    ~ .star {
      color: $accent-color;
    }
  }

  .form-inline-message {
    position: absolute;
    bottom: 0;
    left: 0;
    white-space: nowrap;

    + .form-inline-message {
      display: none;
    }
  }
}

// readonly stars
.stars-medium,
.stars-large {

  .star {
    color: $border-color;

    &.star-full {
      color: $accent-color;
    }
  }
}

.stars-medium {
  .icon-star {
    width: 14px;
    height: 14px;
  }
}

.stars-large {
  .icon-star {
    width: 18px;
    height: 18px;
  }
}

// non-clickable stars on product page
.product-rating-block {
  margin-bottom: 13px;

  .product-rating {
    margin-bottom: 5px;
  }

  .rating-stars {
    display: inline-block;
    margin-right: 8px;
  }

  .total-score {
    font-weight: 600;
  }

  .review-link-block {
    font-size: em(13);
  }
}

.stars-small {
  .icon-star {
    width: 10px;
    height: 10px;
  }

  .star {
    margin-right: 1px;
    color: rgba($body-color, 0.2);

    &.star-full {
      color: $body-color;
    }
  }
}


// capitalize first letter of 'leave a review' link
.has-reviews {
  .review-link {
    display: inline-block;

    &:first-letter {
      text-transform: uppercase;
    }
  }
}

//----- Reviews section on product page ----//

.product-review-summary {
  margin-top: 22px;
  line-height: 30px;

  .rating-stars,
  .rating-summary,
  .review-link {
    display: inline-block;
    vertical-align: middle;
  }

  .rating-stars {
    margin-right: 15px;
  }

  .rating-summary {
    margin-right: 6px;
  }

  .review-link {
    &:first-letter {
      text-transform: uppercase;
    }
  }
}

.review-item {
  margin: 50px 0;

  &.review-item-supplementary {
    display: none;

    .all-reviews-visible & {
      display: block;
    }
  }
}

.review-item-header {
  margin-bottom: 14px;
}

.review-item-stars {
  .rating-stars,
  .review-item-score {
    display: inline-block;
    vertical-align: middle;
  }

  .review-item-score {
    margin-left: 6px;
  }
}

.review-item-title {
  margin: 6px 0;
  font-size: em(15);
}

.review-meta {
  font-size: em(12);
  color: $meta-color;
}

/* scss-combine:product/quick-shop */
.quick-shop-modal-container  { display: none; }

.modal-quick-shop {
  @include breakpoint(s-up) {
    max-width: 1060px;
    padding: 20px;

    .modal-content { padding: 50px; }
  }

  .modal-content {
    opacity: 0;
    transition: opacity 0.2s ease;

    // for fading in once the ajax request has completed
    .modal-wrapper.loaded & { opacity: 1; }
  }

  .modal-body {
    @extend %clearfix;
    @include row(960, 0);
  }

  .quick-shop-product-description {
    margin-bottom: 10px;
    font-size: em(13);
    font-weight: $weight-light;
  }

  .quick-shop-details-link {
    display: inline-block;
    font-size: em(13);
    font-weight: $weight-light;

    &:hover { text-decoration: underline; }
  }

  // overrides where layout differs from single product page
  .product-info-column {
    .product-quantity-submit-columns {
      @extend %clearfix;
      margin: 24px 0 8px;
    }

    .product-quantity-container {
      float: left;
      width: 45%;
      margin-bottom: 0;
    }

    .button.add-to-cart {
      float: left;
      width: 55%;
      margin: 0;
    }

    .wishlist-form {
      @extend %clearfix;
    }

    .button.add-to-wishlist {
      float: right;
      width: 55%;
    }
  }
}

.quick-shop-image-column {
  @include fluid-width(460, 920, 0);
  float: left;

  @include breakpoint(l-up) {
    @include fluid-width(500, 920, 0);
  }

  @include breakpoint(m-only) {
    display: none;
  }
}

.quick-shop-info-column {
  @include breakpoint(m-up) {
    @include fluid-width(460, 920, 0);
    float: left;
    padding-left: 32px;
    border-left: 1px solid $border-color;
  }

  @include breakpoint(l-up) {
    @include fluid-width(420, 920, 0);
  }
}

.quick-shop-images-container {
  min-height: 520px;
  padding-right: 32px;
  margin-right: -1px;
  border-right: 1px solid $border-color;
}

.quick-shop-images {
  text-align: center;

  img {
    display: none;
    max-height: 490px;

    &.active { display: inline-block; }
  }
}

.quick-shop-images-pagination {
  margin-top: 32px;
  text-align: center;
}

.quick-shop-image-thumb {
  display: inline-block;
  max-width: 64px;
  margin-right: 4px;

  a {
    display: block;
    padding: 2px;
    border: 1px solid $container-background-color;

    &:hover {
      border-color: mix($border-color, $container-background-color);
    }
  }

  &.active {
    a {
      border-color: $border-color;
    }
  }
}

.quick-shop-details-lower {
  position: absolute;
  bottom: em(-54, 9);
  left: 50%;
  display: block;
  width: 200px;
  padding: 8px;
  margin-left: -100px;
  font-size: em(9);
  font-weight: 900;
  letter-spacing: 0.02em;
  color: $container-background-color;
  text-align: center;
  text-transform: uppercase;
  border: 1px solid $border-color;
  border-radius: 3px;

  &:hover {
    color: $container-background-color;
    background-color: rgba($container-background-color, 0.2);
    border-color: $container-background-color;
  }
}


/* scss-combine:product/wishlist */
.wishlist-modal {
  display: none;
  position: absolute;
  right: 40px;
  bottom: 0;
  z-index: 1000;
  width: 400px;
  background: $container-background-color;
  box-shadow: 0px 3px 15px rgba(0,0,0,0.2);

  &.animating,
  &.visible {
    display: block;
  }

  &.animating-in {
    transform: translateY(-100px);
    transition: transform 0.3s ease;
  }

  // initial state for leave transition
  &.animating-out {
    opacity: 0;
    transition: opacity 0.2s ease;
  }

  // final states for both in and out
  &.visible {
    transform: translateY(0);
    opacity: 1;
  }

  .form-required .form-field-title {
    display: none;
  }

  .form-field-options .form-label {
    cursor: pointer;
  }

  .pxu-fading-circle {
    z-index: 2;
  }
}

.wishlist-form {
  position: relative;
}

.wishlist-modal-overlay {
  display: none;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 1;
  background-color: rgba(#000, 0.7);
}


// Cart
/* scss-combine:cart/quick-cart */

// widths for desktop version of cart
@include media($min: $mobile-menu-breakpoint) {
  .quick-cart {
    position: relative;
    width: 390px;

    &.empty { width: 244px; }
  }
}

.empty-cart {
  padding: 30px;
  font-size: 13px;
  text-align: center;
  color: lighten($body-color, 20%);

  @include media($max: $mobile-menu-breakpoint) {
    margin-top: 58px;
  }
}

.quick-cart-overlay {
  @extend %fill-parent;
  display: none;
  background-color: rgba($container-background-color, 0.7);
}

.cart-header {
  position: absolute;
  top: 0;
  right: 0;
  left: 0;
  padding: 17px 20px 20px 24px;
  background: $table-heading-background-color;

  // desktop doesn't get the header at all
  @include media($min: $mobile-menu-breakpoint) {
    display: none;
  }

  h3 {
    margin: 0;
    font-size: 16px;

    .count { font-weight: normal; }
  }

  .close-cart {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    padding: 10px 21px;
    font-size: 22px;
    color: contrast($table-heading-background-color, 40%);
  }
}

.cart-content-area {
  overflow: auto;

  @include media($min: $mobile-menu-breakpoint) {
    height: 100%;
    max-height: 288px;
  }

  @include media($max: $mobile-menu-breakpoint) {
    position: absolute;
    top: 58px;
    right: 0;
    bottom: 106px;
    left: 0;
  }
}

.quickcart-item {
  @extend %clearfix;
  position: relative;
  padding: 13px 0;
  border-bottom: 1px solid $border-color;

  @include media($max: $mobile-menu-breakpoint) {
    padding-right: 13px;
    padding-left: 13px;
  }

  @include media($min: $mobile-menu-breakpoint) {
    margin: 0 14px;
  }

  .image-column {
    float: left;
    width: 80px;
    text-align: center;

    img {max-height: 80px;}
  }

  .content-column {
    margin-left: 96px;
  }

  .product-name {
    margin: 7px 0 3px;
    font-family: $body-font-family;
    font-weight: $weight-bold;
  }

  .product-options-summary,
  .product-customizations-summary,
  .product-quantity {
    margin-bottom: 3px;
    font-size: em(13);
    line-height: 1.25;
    color: $meta-color;
  }

  .product-remove { font-size: 13px; }

  .product-price {
    position: absolute;
    right: 13px;
    bottom: 13px;
    font-size: 15px;

    .price-discounted {
      text-decoration: line-through;
      color: $meta-color;
    }
  }

  .quickcart-item-footer { margin-top: 7px; }
}

.cart-footer {
  padding-top: 14px;
  padding-bottom: 14px;
  background: $container-background-color;
  border-top: 1px solid transparent;

  @include media($max: $mobile-menu-breakpoint) {
    position: absolute;
    right: 0;
    bottom: 0;
    left: 0;
    border-top: 1px solid $border-color;
  }

  @include media($min: $mobile-menu-breakpoint) {
    padding-right: 14px;
    padding-left: 14px;
  }

  // add border-top to footer if cart has three items or more
  .scrollable & { border-color: $border-color; }

  // and then remove it again if we've scrolled to the bottom
  .bottomed & { border-color: transparent; }

  .subtotal {
    @extend %clearfix;
    margin-bottom: 16px;

    .label,
    .total {
      line-height: em(17);
    }

    .label {
      float: left;
      padding-top: em(2, 14);
      font-size: em(14);
    }

    .total {
      float: right;
      font-size: em(17);
    }
  }

  .actions { @extend %clearfix; }

  .button {
    padding: em(9, 13) 4px;
    font-size: em(13);

    &.view-cart-button {
      @include fluid-width(160, 390, 0);
      float: left;
    }

    &.checkout-button {
      @include fluid-width(215, 390, 0);
      float: right;
    }
  }
}

/* scss-combine:cart/cart-page */
.cart-item-details-header,
.cart-item-details {
  .cell {
    float: left;
    width: 33.333%;
  }

  .cart-header-quantity,
  .cart-content-quantity { text-align: center; }

  .cart-header-total,
  .cart-content-total { text-align: right; }
}

// the th-style heading, only for desktop
.cart-table-header {
  @extend %clearfix;
  font-size: em(12);
  color: contrast($table-heading-background-color, 40%);
  background-color: $table-heading-background-color;

  @include breakpoint(m-only) { display: none; }

  .cell {
    padding: 10px 9px 9px;
    line-height: 1;
  }

  .cart-header-product { float: left; }

  .cart-item-details-header {
    float: right;
    width: 300px;
  }
}

.cart-table { position: relative; }

.cart-table-loading-overlay {
  @extend %fill-parent;
  display: none;
  background-color: rgba($container-background-color, 0.4);

  .cart-processing & { display: block;}
}

//----- repeated product rows ----//

.cart-table-item {
  @extend %clearfix;
  padding: 32px 0;
  margin-bottom: -1px;
  border-top: 1px solid $border-color;
  border-bottom: 1px solid $border-color;

  @include breakpoint(m-up) {
    padding: 16px 0;
  }

  &:first-child { border-top: 0;}

  // revealer classes
  &.animating {
    opacity: 1;
    transition: opacity 0.3s ease;
  }

  &.animating-out { opacity: 0; }

  .image-column {
    float: left;
    width: 60px;
    text-align: center;

    @include breakpoint(s-up) {
      width: 120px;
    }

    // TODO: possibly awaiting better image size management
    img { max-height: 120px; }
  }

  .main-column {
    position: relative;
    margin-left: 74px;

    @include breakpoint(s-up) {
      margin-left: 142px;
    }

    @include breakpoint(m-up) {
      padding: 17px 0;
    }
  }

  .cart-item-meta {
    @include breakpoint(m-up) {
      margin-right: 300px;
    }

    @include breakpoint(m-only) {
      margin-bottom: 20px;
    }
  }

  .product-name {
    margin: 0 0 3px;
    color: $body-color;
  }

  .cart-item-detail {
    margin-bottom: 3px;
    font-size: em(13);
    color: $meta-color;

    &:last-of-type {
      margin-bottom: 0;
    }
  }

  // edit & remove links
  .actions {
    font-size: em(13);

    .divider { color: $border-color; }
  }

  .cart-item-details-container {
    @include breakpoint(m-up) {
      position: absolute;
      top: 17px;
      right: 0;
      width: 300px;
    }
  }

  // per-product header to show on smaller screens when main header is hidden
  .cart-item-details-header {
    margin-bottom: 10px;
    overflow: hidden;
    font-size: em(12);
    line-height: 1;
    color: contrast($table-heading-background-color, 40%);
    background-color: $table-heading-background-color;

    @include breakpoint(m-up) { display: none; }

    .cell { padding: 7px 10px; }
  }

  .cart-item-details {
    @extend %clearfix;

    @include breakpoint(m-only) {
      font-size: em(12);
    }

    .cell {
      line-height: 1;
    }
  }

  .cart-header-each { padding-left: 0; }

  .cart-header-total { padding-right: 0; }

  .cart-header-price { padding-top: 3px; }

  .price {
    padding-top: 5px;
  }

  .price-discounted {
    text-decoration: line-through;
    color: $meta-color;
  }

  .form-input.quantity-input {
    width: 46px;
    height: 31px;
    padding: 0;
    margin: 0 auto;
    text-align: center;
  }

  .update-quantity {
    display: inline-block;
    padding: 6px 4px;
    font-size: em(11);
    visibility: hidden;

    &.visible {
      visibility: visible;
    }
  }
}


//----- cart totals column with pre-checkout tools ----//

.cart-table-footer {
  position: relative;
  margin-top: 30px;

  @include breakpoint(m-up) {
    float: right;
    width: 300px;
  }

  .input-appended {
    .form-input-wrapper { right: 75px; }

    .button {
      width: 65px;
      font-size: em(11);
    }

    .form-input,
    .button {
      height: rem(37);
    }
  }

  .cart-footer-loading-overlay {
    @extend %fill-parent;
    display: none;
    background-color: rgba($container-background-color, 0.4);
  }

  .cart-processing &,
  &.cart-processing {
    .cart-footer-loading-overlay { display: block; }
  }

  // don't show spinner icon if we're fading out both table and footer (table has its own spinner)
  .cart-processing & {
    .pxu-fading-circle { display: none; }
  }
}

.checkout-multiple {
  display: block;
  clear: both;
  padding-top: 15px;
  text-align: center;
}

.price-totals-table {
  width: 100%;
  margin-top: 20px;
  font-weight: 300;
  border: 0;
  border-collapse: collapse;

  tbody { border-bottom: 12px solid transparent; }

  td {
    padding: 0 0 3px;
    vertical-align: top;
    border: 0;

    &.faded { color: lighten($body-color, 50%); }
  }

  .value { text-align: right; }

  .order-total {
    td {
      padding-top: 10px;
      font-weight: 600;
      border-top: 1px solid $border-color;
    }
  }
}

.coupon-code-apply-form,
.gift-certificate-apply-form {
  margin-bottom: 14px;
}

.gift-cert-meta {
  font-size: em(11);
}

.cart-action-minor {
  font-size: em(12);
  text-align: right;

  a { cursor: pointer; }
}


//----- shipping calculator ----//

.shipping-calculator {
  display: none;
  padding: $gutter/2 0 $gutter;

  &.visible { display: block; }
}

.show-calculator { cursor: pointer; }

.calculator-form {
  margin: 0 (-1 * $gutter/2);

  .form-field {
    padding: 0 $gutter/2;
  }

  .calculator-field-zip,
  .calculator-field-city {
    float: left;
    width: 50%;
  }

  .form-action {
    padding-top: 12px;
    clear: left;
  }
}

.button-shipping-estimate-submit {
  padding-top: 6px;
  padding-bottom: 4px;
}

.shipping-methods-choice { margin-top: 20px; }

.shipping-methods-heading {
  margin-bottom: 4px;
  font-size: em(12);
}

.shipping-methods-container {
  padding: 13px 16px;
  margin-bottom: 15px;
  background-color: $table-stripe-background-color;

  .form-field:last-child { margin-bottom: 0; }
}

.shipping-provider-section {
  border-top: 1px solid contrast($table-stripe-background-color, 5%);
  padding-top: 11px;
  margin-top: 8px;
}

.input-shipping-quote { margin-right: 4px; }

.shipping-provider-logo {
  margin-right: 2px;
}

.button-select-shipping-quote {
  padding-top: 6px;
  padding-bottom: 4px;
}

.gift-card-image {
  padding: $gutter;
  color: contrast($container-background-color, 10%);
  background-color: contrast($container-background-color, 4%);
}

// workaround to override funky formatting of paypal button
.additional-checkout-buttons {
  @extend %clearfix;
  margin-top: 15px;
  text-align: center;

  // scss-lint:disable ImportantRule, QualifyingElement
  p { float: none !important; }

  form { clear: both;}
}


//----- empty cart message -----//

.message-cart-empty {
  padding-top: $gutter * 2;
  padding-bottom: $gutter * 2;
  margin-top: $gutter * 2;
  text-align: center;

  &.hidden { display: none; }
}

/* scss-combine:cart/gift-wrap */
// ------ Gift-wrapping block w/in cart line item ------ //

.item-gift-wrap {
  font-size: em(13);
  color: $meta-color;
}

.item-gift-wrap-change {
  font-size: em(11, 12);

  .item-gift-wrap-trigger,
  .item-gift-wrap-remove {
    color: $meta-color;

    &:hover { text-decoration: underline; }
  }
}

.item-gift-wrap-add {
  padding: 0;
  text-decoration: underline;
  background: none;
  border: 0;
}


// ------ Gift-wrap modal ------ //

.gift-wrap-modal-container { display: none; }

.modal-gift-wrap {
  @include breakpoint(s-only) { font-size: 0.9em; }

  @include breakpoint(s-up) {
    max-width: 600px;
    padding: 20px;

    background-image: url("../images/spinner-large.svg");
    background-position: center center;
    background-repeat: no-repeat;

    .modal-content {
      padding: 50px 50px 34px;
      opacity: 0;
      transition: opacity 0.1s ease;

      // for fading in once the ajax request has completed
      .modal-wrapper.loaded & { opacity: 1; }
    }
  }
}

.gift-wrap-heading {
  @extend %heading-medium;
  margin: 0 0 16px;
  text-align: center;
}

.gift-wrap-multiple {
  max-height: 428px;
  overflow-y: auto;
}

.gift-wrap-single,
.gift-wrap-multiple,
.gift-wrap-image-preview {
  display: none;

  &.visible { display: block; }
}

.gift-wrap-image-preview {
  @extend %clearfix;
}

.gift-wrap-image-label {
  float: left;
  width: 30%;
  padding-right: $gutter;
  font-size: em(13);
}

.gift-wrap-hover-for-preview {
  font-size: em(11);
  color: $meta-color;
}

.gift-wrap-image {
  position: relative;
  display: block;
  float: left;
  width: 70%;
  height: $gutter * 2;
  overflow: hidden;
  border-radius: 3px;

  img {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    transform: translateY(-50%);

    &.cover-hover { transform: translateY(0); }
  }
}

.gift-wrap-message {
  display: none;

  &.visible { display: block; }
}

/* scss-combine:cart/gift-certificate */
// ------ Gift certificate subnav ------ //

.giftcard-nav {
  margin-bottom: $gutter * 3;
  padding: 0;
  font-size: em(13);
  text-align: center;
}

.giftcard-nav-item {
  display: inline-block;
  margin: 0 $gutter/2;

  &.is-active {
    color: $meta-color;
  }

  &[href]:hover { text-decoration: underline; }
}

.certificate-message {
  min-height: 166px;
}

.certificate-form-actions {
  margin-top: $gutter * 2;
  text-align: center;

  @include breakpoint(s-only) {
    margin-top: $gutter;

    .button {
      width: 100%;
      margin: 0 0 $gutter / 2;
    }
  }
}

.form-certificate-balance {
  .input-appended {
    .button { width: 160px;}

    .form-input-wrapper {
      right: 168px;
    }
  }
}

// workaround so that only a single error surfaces when radio group is invalid https://github.com/casperin/nod/issues/115
.certificate-theme-select {
  .form-field-error:not(:last-child) {
    .form-inline-message { display: none; }
  }
}

/* scss-combine:cart/apple-pay */
// scss-lint:disable ColorKeyword, ColorVariable
.apple-pay-checkout-button {
  display: none;
  height: rem(50);
  background: {
    position: 50% 50%;
    repeat: no-repeat;
    size: 100% 60%;
  }
  border-radius: 5px;

  @if stencilString("style_apple_pay_button") == "white" {
    background-color: white;
    background-image: -webkit-named-image(apple-pay-logo-black);
  } @else if stencilString("style_apple_pay_button") == "white-border" {
    background-color: white;
    background-image: -webkit-named-image(apple-pay-logo-black);
    border: 0.5px solid black;
  } @else {
    background-image: -webkit-named-image(apple-pay-logo-white);
    background-color: black;
  }

  .additional-checkout-buttons & {
    margin-bottom: rem(15);
  }

  .apple-pay-supported & {
    display: block;
  }
}

/* scss-combine:cart/edit-options */
.cart-options-modal {
  max-width: 600px;
  opacity: 0;

  .modal-wrapper.loaded & {
    opacity: 1;
  }

  .reconfigure-errors {
    display: none;
  }

  .cart-item-edit-product-title {
    @extend %heading-medium;
    margin: 0 0 16px;
    text-align: center;
  }

  .modal-content {
    padding: 50px 50px 34px;
  }
}

.cart-item-edit-button {
  display: block;
  margin-bottom: 5px;
  font-size: em(13);
}


// Core overrides
/* scss-combine:general/account */
// scss-lint:disable PlaceholderInExtend


.account-button-primary {
  @extend .button-primary;
}

.account-button-secondary {
  @extend .button-secondary;
}

.account-button-small {
  padding: 2px 6px;
  font-size: rem(12);
  font-weight: normal;
}


.account-heading {
  @extend %heading-medium;
  padding-bottom: 0.5em;
}

.account-sub-heading {
  @extend %heading-small;
}

.form-required {
  .form-field-title {
    &:after {
      font-size: em(12);
      color: $error-color;
      content: "*";
    }
  }

  .required-text {
    @extend .show-for-sr;
  }
}

.account-nav-link {
  padding: 10px 15px;

  &:hover {
    background: mix($body-color, $container-background-color, 2%);
  }
}


// Create account

.account-create-form,
.account-settings-form,
.account-address-form {
  .form-field {
    &:nth-of-type(odd) { clear: left; }
  }
}


// account product grids

.account-product-wrapper {
  @extend %clearfix;
}

.account-product-item {
  @extend .product-item;
}

.account-product-item-thumbnail {
  padding: 16px;
  margin-bottom: 10px;

  img { max-height: 200px; }
}

.account-product-item-title {
  margin: 10px 10px 2px;
  font-weight: 700;

  a { color: $headings-color; }
}

.account-product-item-brand {
  a { color: $meta-color; }
}

.account-product-item-price {
  .price-withouttax,
  .price-withtax {
    display: block;
  }

  .price-rrp { text-decoration: line-through; }

  .price-tax-label { font-size: em(10); }
}

.account-product-item-remove {
  input[type="submit"] {
    position: absolute;
    top: 16px;
    right: 16px;
    width: em(22, 22);
    height: em(22, 22);
    padding: 0;
    font-size: rem(26);
    font-weight: normal;
    line-height: 1;
    color: $error-color;
    background: transparent;
    border: 0;
  }
}

// account list overrides

.account-list-header {
  background: $table-heading-background-color;
}

.account-list-header-label {
  font-size: rem(12);
}

.account-list-item {
  padding: 12px 0;
  border-bottom: 1px solid $border-color;
}

.account-wishlists {
  .account-list-header-item,
  .account-list-item-cell {
    width: 18%;
    padding: 4px 8px;

    &:last-child {
      width: 46%;
    }
  }
}


// wishlists

.account-share-wishlist-url {
  padding: 24px;
  background: $table-heading-background-color;

  .account-sub-heading {
    margin-top: 0;
  }
}

// account pagination

.account-content {
  .pagination {
    display: table;
    width: 100%;
    margin-top: $gutter * 2;
    font-weight: 300;
  }

  .pagination-item {
    display: table-cell;
    width: 1%;
    white-space: nowrap;
  }

  .pagination-count {
    display: table-cell;
    text-align: center;
  }

  .pagination-next {
    text-align: right;
  }
}

// Account payment method
[data-payment-method-form] {
  .button:not(.button-primary) {
    @extend .button-secondary;
  }
}

.payment-method-form {
  .payment-method-form-inputs-icon {
    top: 31px !important; //override core position top
  }
}

.payment-method-edit-form {
  @media screen and (min-width: 619px) and (max-width: 819px) {
    .button {
      + .button {
        margin-top: 5px;
        margin-left: 0;
      }
    }
  }
}
