components/header/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
  <div class="currency-selector-wrapper select-input-wrapper">
    <span class="form-selected-text">{{currency_selector.active_currency_code}}</span>
    <select class="form-input" data-currency-selector>
      {{#each currency_selector.currencies}}
        <option value="{{{switch_url}}}" {{#if is_active}}selected{{/if}}>
          {{name}}
        </option>
      {{/each}}
    </select>
  </div>
{{else}}
  <div class="currency-selector-wrapper single-currency">
    {{currency_selector.active_currency_code}}
  </div>
{{/if}}
